INSERT INTO `products` VALUES (555, 'Apple iPad Pro 11 2021 M1 WiFi 2TB', 'apple-ipad-pro-11-2021-m1-wifi-2tb', 110, 39000000, 52000000, 0, '<div class=\"blog-content\">\r\n<h2 style=\"text-align:justify\"><strong>iPad Pro 2021 11 inch &ndash; Tablet cấu m&igrave;nh mạnh với chip M1 mới</strong></h2>\r\n\r\n<p style=\"text-align:justify\">Như thường lệ hằng năm Apple lại cho ra mắt chiếc m&aacute;y t&iacute;nh bảng của m&igrave;nh.&nbsp;<strong>iPad Pro&nbsp;2021 </strong>phi&ecirc;n bản&nbsp;11 inch hứa hẹn mang đến một trải nghiệm mạnh vẽ với nhiều t&iacute;nh năng nổi bật.</p>\r\n\r\n<h3 style=\"text-align:justify\"><strong>Thiết kế vu&ocirc;ng vức sang trọng, m&agrave;n h&igrave;nh 11 inch</strong></h3>\r\n\r\n<p style=\"text-align:justify\">iPad Pro 11 inch 2021 sở hữu ngoại h&igrave;nh mang nhiều điểm tương tự về ngoại h&igrave;nh với chiếc iPad Pro trước đ&oacute;.&nbsp;<a href=\"https://cellphones.com.vn/tablet/ipad-pro.html\">Apple iPad Pro 2021</a> sử dụng m&agrave;n h&igrave;nh k&iacute;ch thước 11 inch v&agrave; sử dụng m&agrave;n h&igrave;nh LCD truyền thống.&nbsp; M&agrave;n h&igrave;nh tr&ecirc;n iPad Pro 2021 n&agrave;y với c&ocirc;ng nghệ m&agrave;u&nbsp;ProMotion c&ugrave;ng độ ph&acirc;n giải cao&nbsp;264 pixel mỗi inch. M&agrave;n h&igrave;nh n&agrave;y cũng được trang bị lớp phủ chống b&aacute;m v&acirc;n tay v&agrave; chống phản xạ, n&acirc;ng cao trải nghiệm cho người d&ugrave;ng.</p>\r\n\r\n<p style=\"text-align:justify\"><img alt=\"Một vài điểm khác biệt từ ngoại hình, màn hình 11 inches, Mini-LED\" class=\"cpslazy loaded\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/tablet/apple/ipad-pro-11-2021-1.jpg\" style=\"height:600px; width:100%\" /></p>\r\n\r\n<h3 style=\"text-align:justify\"><strong>Sức mạnh kinh ngạc từ con chip Apple M1</strong></h3>\r\n\r\n<p style=\"text-align:justify\">iPad Pro 11 2021 được cung cấp sức mạnh bởi con chip M1 với CPU 8 nh&acirc;n v&agrave; GPU 8 nh&acirc;n. Nhờ đ&oacute; m&agrave; thế hệ iPad Pro năm nay nhanh hơn rất nhiều lần so với iPad 2010 ban đầu v&agrave; nhanh hơn tới 50% so với iPad Pro 2020. Theo nh&agrave; sản xuất cho biết, những c&aacute;i tiến n&agrave;y gi&uacute;p m&aacute;y thực hiện chỉnh sửa video 4K v&agrave; thiết kế 3D tốt hơn.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">iPad Pro 2021 được trang bị đến 4 loa, khung nh&ocirc;m với chấu sạc từ t&iacute;nh d&agrave;nh cho Apple Pencil nằm ở hai b&ecirc;n h&ocirc;ng cũng như cổng USB-C nằm ở cạnh b&ecirc;n dưới để sạc.</p>\r\n\r\n<h3 style=\"text-align:justify\">Cụm camera 12MP mang lại h&igrave;nh ảnh chất lượng</h3>\r\n\r\n<p style=\"text-align:justify\">M&aacute;y t&iacute;nh bảng&nbsp;iPad Pro 2021 được trang bị cụm camera sau với cacmera g&oacute;c si&ecirc;u rộng 12MP với g&oacute;c chụp 122 độ. B&ecirc;n cạnh l&agrave; camera si&ecirc;u rộng 10MP cuối c&ugrave;ng l&agrave; m&aacute;y qu&eacute;t LiDAR. Camera trước tr&ecirc;n m&aacute;y với khẩu độ 12MP, hỗ trợ g&oacute;c chụp l&ecirc;n đến 122 độ.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">Camera tr&ecirc;n iPad&nbsp;Pro 2021 với t&iacute;nh năng&nbsp;Smart HDR 3 mang lại khả năng chụp ảnh v&agrave; quay video một c&aacute;ch to&agrave;n diện.</p>\r\n\r\n<h3><strong>Kết nối kh&ocirc;ng d&acirc;y nhanh ch&oacute;ng với wifi 6 v&agrave; 5G</strong></h3>\r\n\r\n<p>M&aacute;y t&iacute;nh bảng iPad Pro 2021 với hệ thống wifi 6 mang lại khả năng kết nối si&ecirc;u nhanh. Ngo&agrave;i ra m&aacute;y c&ograve;n được trang bị kết nối 5G thế hệ mới magn lại khả năng truyền tải nhanh vượt trội.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Về khả năng giải tr&iacute;, iPad Pro 2021 với hệ thống 4 loa Dolby Atmos mang đến &acirc;m thanh chất lượng như trong rạp h&aacute;t. Nhờ vậy người d&ugrave;ng c&oacute; thể thoải m&aacute;i trải nghiệm nghe nhạc, xem phim với chất lượng &acirc;m thanh sống động.</p>\r\n\r\n<h3><strong>Hỗ trợ Apple Pencil c&ugrave;ng hệ điều h&agrave;nh iPad OS mượt m&agrave;</strong></h3>\r\n\r\n<p>Tablet iPad Pro 2021 c&oacute; thể kết nối với Apple Pencil gi&uacute;p người d&ugrave;ng sử dụng ghi ch&uacute; tốt hơn. Sự kết hợp n&agrave;y mang lại cảm gi&aacute;c như trực quan v&agrave; ch&iacute;nh x&aacute;c như đang ghi ch&uacute;, vẽ tranh tr&ecirc;n giấy.&nbsp;Thiết kế Apple Pencil với khả năng gắn từ t&iacute;nh c&ugrave;ng đầu b&uacute;t c&oacute; độ nghi&ecirc;ng v&agrave; độ nhạy cao, tối ưu cho trải nghiệm người d&ugrave;ng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>L&agrave; một hệ điều h&agrave;nh do ch&iacute;nh Apple sản xuất n&ecirc;n iPadOS kh&aacute; giống iOS. Người d&ugrave;ng iPad Pro 2021 sẽ c&oacute; thể đa nhiệm một c&aacute;ch dễ d&agrave;ng. C&aacute;c thao t&aacute;c như d&aacute;n v&agrave; sao ch&eacute;p cũng được thực hiện một c&aacute;ch dễ d&agrave;ng hơn.</p>\r\n\r\n<p>iPad Pro 2021 c&ograve;n sở hữu kho ứng dụng v&ocirc; c&ugrave;ng phong ph&uacute; với hơn 1 triệu ứng dụng kh&aacute;c nhau tr&ecirc;n AppStore.</p>\r\n</div>', '{\"Kích thước màn hình\":\"10.9 inches\",\"Công nghệ màn hình\":\"IPS LCD\",\"Camera sau\":\"12 MP góc rộng,khẩu độ f\\/1.8\",\"Camera trước\":\"7 MP, khẩu độ f\\/2.2\",\"Chipset\":\"Apple A14 Bionic\",\"Bộ nhớ trong\":\"64 GB\",\"Pin\":\"Khoảng 7600 mAh\",\"Hệ điều hành\":\"iOS\",\"Độ phân giải màn hình\":\"1640 x 2360 pixels\",\"Loại CPU\":\"2 nhân 3.0GHz, 4 nhân 1.8GHz\",\"GPU\":\"Apple GPU\",\"Quay video\":\"Trước: 1080p@30\\/60fps, Sau: 4K@24\\/30\\/60fps, 1080p@30\\/60\\/120\\/240fps;\",\"Kích thước\":\"Dài 247.6 mm - Ngang 178.5 mm - Dày 6.1 mm\",\"Trọng lượng\":\"458 g\",\"Chất liệu mặt lưng\":\"Kim loại\",\"Công nghệ sạc\":\"Sạc nhanh 20W\",\"Wi-Fi\":\"802.11ax Wi-Fi 6, dual-band, hotspot\",\"Bluetooth\":\"Bluetooth 5.0\",\"GPS\":\"GPS\\/GNSS\"}', 1, 3, 5, 1, '2021-07-13 15:36:57', '2021-07-17 11:54:49', NULL);
INSERT INTO `products` VALUES (556, 'iPad Pro 11 2020 WiFi 128GB', 'ipad-pro-11-2020-wifi-128gb', 110, 16000000, 21990000, 0, '<div class=\"blog-content\">\r\n<h2>iPad Pro 11 &nbsp;&ndash; Sự n&acirc;ng cấp đ&aacute;ng gi&aacute; sau hai năm</h2>\r\n\r\n<p>Bắt đầu được giới thiệu từ lần đầu v&agrave;o năm 2015, iPad Pro l&agrave; d&ograve;ng tablet cao cấp nhất của Apple. Ở phi&ecirc;n bản 2018, iPad Pro đ&atilde; sở hữu m&agrave;n h&igrave;nh viền mỏng c&ugrave;ng hệ thống nhận diện khu&ocirc;n mặt Face ID. Cho đến hai năm sau, Apple cho ra mắt <strong>iPad Pro 11 WiFi 2020 128gb</strong> được n&acirc;ng cấp mạnh mẽ hơn về hiệu năng v&agrave; camera.</p>\r\n\r\n<p>V&agrave; mới đ&acirc;y, Apple đ&atilde; cho ra mắt <a href=\"https://cellphones.com.vn/ipad-pro-11-2021.html\">iPad Pro 11 inch 2021</a> với chip M1 cực mạnh sắp được b&aacute;n tại CellphoneS với mức gi&aacute; ưu đ&atilde;i.</p>\r\n\r\n<h3>Kết nối mạng qua wifi ổn định, kh&ocirc;ng hỗ trợ 4G</h3>\r\n\r\n<p>Ở phi&ecirc;n bản n&agrave;y, chiếc m&aacute;y sử dụng phương thức kết nối chủ yếu l&agrave; WiFi, kh&ocirc;ng d&ugrave;ng SIM điện thoại. Khi iPad Pro 11 WiFi 2020 được kết nối với đường truyền WiFi, người d&ugrave;ng c&oacute; thể sử dụng thiết bị để truy cập website, c&aacute;c ứng dụng, call video hoặc thực hiện t&aacute;c vụ cần thiết tr&ecirc;n mạng internet.</p>\r\n\r\n<p><img alt=\"Kết nối mạng qua wifi ổn định, không hỗ trợ 4G\" class=\"cpslazy loaded\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/tablet/apple/apple-ipad-pro-11-2020-wifi-128-gb-1.jpg\" style=\"height:600px; width:100%\" /></p>\r\n\r\n<p>Dung lượng bộ nhớ trong lu&ocirc;n l&agrave; thứ khiến cho người d&ugrave;ng quan t&acirc;m h&agrave;ng đầu khi dự định sở hữu bất kỳ sản phẩm c&ocirc;ng nghệ n&agrave;o. Với iPad Pro 11 WiFi 2020, bạn sẽ được cung cấp một bộ nhớ lưu trữ l&ecirc;n đến 128GB. Ở mức dung lượng n&agrave;y, bạn c&oacute; thể thoải m&aacute;i lưu trữ h&igrave;nh ảnh, t&agrave;i liệu, video clip, file b&agrave;i h&aacute;t,&hellip; trong suốt một thời gian d&agrave;i m&agrave; kh&ocirc;ng cần lo lắng việc hết bộ nhớ. Ngo&agrave;i 128GB th&igrave;&nbsp;iPad Pro 11 WiFi 2020 c&ograve;n nhiều phi&ecirc;n bản với dung lượng lưu trữ kh&aacute;c như:&nbsp;iPad Pro 11 WiFi 2020 256GB.</p>\r\n\r\n<h3>Thiết kế quen thuộc, trọng lượng chỉ 471g, m&agrave;n h&igrave;nh Liquid Retina, độ ph&acirc;n giải 1668 x 2388 pixel</h3>\r\n\r\n<p>Thiết kế của iPad Pro 11 128gb giống phi&ecirc;n bản trước v&agrave; một thay đổi duy nhất nằm ở mặt lưng với cụm camera sau h&igrave;nh vu&ocirc;ng. Tổng k&iacute;ch thước của chiếc iPad n&agrave;y l&agrave; 247.6 x 178.5 x 5.9 mm v&agrave; c&oacute; trọng lượng 471g chỉ nhỉnh hơn một ch&uacute;t so với phi&ecirc;n bản trước. Đồng thời c&aacute;c cạnh v&agrave; g&oacute;c xung quanh được bo tr&ograve;n mềm mại sẽ tạo cho người d&ugrave;ng cảm gi&aacute;c cầm nắm v&ocirc; c&ugrave;ng chắc chắn v&agrave; thoải m&aacute;i.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>iPad Pro 11 WiFi 2020 sở hữu m&agrave;n h&igrave;nh Retina k&iacute;ch thước 11 inch IPS với độ ph&acirc;n giải 1668 x 2388 pixel. M&agrave;n h&igrave;nh c&ograve;n được sử dụng c&ocirc;ng nghệ m&agrave;n h&igrave;nh ProMotion cho ph&eacute;p người d&ugrave;ng được trải nghiệm những h&igrave;nh ảnh một c&aacute;ch ch&acirc;n thực v&agrave; sinh động nhất.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>B&ecirc;n cạnh những c&ocirc;ng nghệ quen thuộc, với iPad Pro 11 WiFi 2020 được phủ c&ocirc;ng nghệ oleophobic chống v&acirc;n tay, chống phản xạ cho khả năng hiển thị ngo&agrave;i trời tốt. c&ograve;n mang đến cho chiếc m&agrave;n h&igrave;nh của sản phẩm n&agrave;y c&ocirc;ng nghệ <span style=\"color:#333300\"><span style=\"color:#333300\">True Tone</span></span>. iPad Pro 11 2020 cũng được trang bị c&ocirc;ng nghệ True Tone n&agrave;y gi&uacute;p m&agrave;n h&igrave;nh trở n&ecirc;n thực tế hơn, ph&ugrave; hợp với những người chỉnh sửa ảnh hoặc chỉnh sửa video muốn tinh chỉnh m&agrave;u sắc của h&igrave;nh ảnh. Ngo&agrave;i ra, tốc độ của m&agrave;n h&igrave;nh đạt 120 Hz, đem đến khả năng hiển thị li&ecirc;n tục 120 khung h&igrave;nh mỗi gi&acirc;y.</p>\r\n\r\n<h3>Hiệu năng n&acirc;ng cấp: vi xử l&yacute; Apple A12Z Bionic mạnh mẽ</h3>\r\n\r\n<p><strong>iPad Pro 11 2020</strong> được cung cấp sức mạnh bởi con chip A12Z Bionic, gồm GPU 8 l&otilde;i c&ugrave;ng kiến tr&uacute;c kiểm so&aacute;t nhiệt mới v&agrave; hệ thống controller được tối ưu h&oacute;a nhằm cho hiệu suất cao hơn. Theo nh&agrave; sản xuất cho biết, nhờ những cải tiến tr&ecirc;n m&agrave; c&aacute;c chuy&ecirc;n gia l&agrave;m việc tr&ecirc;n <a href=\"https://cellphones.com.vn/tablet/ipad-pro.html\" target=\"_blank\">iPad Pro 2020</a> c&oacute; thể chỉnh sửa <span style=\"color:#333300\"><span style=\"color:#333300\">video 4K</span></span> v&agrave; thiết kế m&ocirc; h&igrave;nh 3D trơn tru.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Sở hữu vi&ecirc;n pin lithium-polymer, iPad Pro 11 wifi 2020 cho 10 giờ lướt web. iPad Pro 2020 hỗ trợ sử dụng Apple Pencil cho ph&eacute;p ghi ch&uacute; dễ d&agrave;ng như tr&ecirc;n giấy. Đồng thời thỏa sức s&aacute;ng tạo, chỉnh sửa h&igrave;nh ảnh.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3>Camera sau được n&acirc;ng cấp: 12MP+10MP, m&aacute;y qu&eacute;t LiDAR</h3>\r\n\r\n<p>iPad Pro 2020 được trang bị đến tận hệ thống ba camera sau, bao gồm cảm biến ch&iacute;nh 12MP v&agrave; ống k&iacute;nh g&oacute;c si&ecirc;u rộng 10MP. Cảm biến thứ ba được Apple gọi l&agrave; m&aacute;y qu&eacute;t LiDAR, cho ph&eacute;p đo được khoảng c&aacute;ch l&ecirc;n tới 5 m&eacute;t xung quanh vật thể, hoạt động cả ngo&agrave;i trời v&agrave; trong nh&agrave;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Điều n&agrave;y cho thấy sự đầu tư rất nhiều của Apple đối với sản phẩm iPad khi c&ocirc;ng nghệ camera kh&ocirc;ng thua k&eacute;m g&igrave; so với camera tr&ecirc;n iPhone. Thậm ch&iacute; l&agrave; bạn c&ograve;n c&oacute; thể sử dụng camera tr&ecirc;n iPad thay thế cho những chiếc m&aacute;y ảnh DSLR. Camera tr&ecirc;n iPad Pro 2020 hỗ trợ quay video 4K.</p>\r\n</div>', '{\"Kích thước màn hình\":\"10.9 inches\",\"Công nghệ màn hình\":\"IPS LCD\",\"Camera sau\":\"12 MP góc rộng,khẩu độ f\\/1.8\",\"Camera trước\":\"7 MP, khẩu độ f\\/2.2\",\"Chipset\":\"Apple A14 Bionic\",\"Bộ nhớ trong\":\"64 GB\",\"Pin\":\"Khoảng 7600 mAh\",\"Hệ điều hành\":\"iOS\",\"Độ phân giải màn hình\":\"1640 x 2360 pixels\",\"Loại CPU\":\"2 nhân 3.0GHz, 4 nhân 1.8GHz\",\"GPU\":\"Apple GPU\",\"Quay video\":\"Trước: 1080p@30\\/60fps, Sau: 4K@24\\/30\\/60fps, 1080p@30\\/60\\/120\\/240fps;\",\"Kích thước\":\"Dài 247.6 mm - Ngang 178.5 mm - Dày 6.1 mm\",\"Trọng lượng\":\"458 g\",\"Chất liệu mặt lưng\":\"Kim loại\",\"Công nghệ sạc\":\"Sạc nhanh 20W\",\"Wi-Fi\":\"802.11ax Wi-Fi 6, dual-band, hotspot\",\"Bluetooth\":\"Bluetooth 5.0\",\"GPS\":\"GPS\\/GNSS\"}', 1, 3, 5, 1, '2021-07-13 15:40:06', '2021-07-17 11:54:49', NULL);
INSERT INTO `products` VALUES (557, 'Apple MacBook Pro 13 Touch Bar M1 256GB 2020', 'apple-macbook-pro-13-touch-bar-m1-256gb-2020', 60, 29000000, 35990000, 0, '<div class=\"blog-content\">\r\n<h2><strong>Macbook Pro M1 2020 &ndash; Hiệu năng vượt trội với chip M1 mạnh mẽ</strong></h2>\r\n\r\n<p>M&aacute;y t&iacute;nh bảng<strong> Macbook Pro M1</strong>&nbsp;thế hệ mới vừa ra mắt được trang bị vi xử l&yacute; M1 do ch&iacute;nh Apple tự thiết kế. Hứa hẹn mang đến hiệu năng cực k&igrave; mạnh mẽ, thời gian sử dụng cực k&igrave; ấn tượng.</p>\r\n\r\n<h3><strong>Thiết kế trau chuốt từng đường n&eacute;t, trải nghiệm h&igrave;nh ảnh sắc n&eacute;t với m&agrave;n h&igrave;nh Retina</strong></h3>\r\n\r\n<p>Vẫn kế thừa thiết kế từ người tiềm nhiệm trước đ&oacute; của m&igrave;nh, <a href=\"https://cellphones.com.vn/laptop/mac/macbook-pro.html\">Macbook Pro 2020</a> M1 vẫn sở hữu thiết kế nh&ocirc;m nguy&ecirc;n khối, c&aacute;c đường viền tr&ecirc;n m&aacute;y được cắt CNC tỉ mỉ, sắc xảo.</p>\r\n\r\n<p><img alt=\"Thiết kế tinh tế\" class=\"cpslazy loaded\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/laptop/macbook/macbook-pro-touch-bar-m1-1.jpg\" style=\"height:600px; width:100%\" /></p>\r\n\r\n<p>M&agrave;n h&igrave;nh Retina 13.3 inches sắc n&eacute;t, tấm nền LED mang đến m&agrave;u đen s&acirc;u. Hỗ trợ dải m&agrave;u rộng P3, c&ocirc;ng nghệ True Tone c&acirc;n bằng m&agrave;u trắng tự động để ph&ugrave; hợp với nhiệt độ m&agrave;u của &aacute;nh s&aacute;ng tự nhi&ecirc;n xung quanh, đem đến trải nghiệm hiển thị tự nhi&ecirc;n nhất.</p>\r\n\r\n<h3><strong>Phản hồi si&ecirc;u tốc với vi xử l&yacute; M1 mạnh mẽ, macOS Big Sur nhiều t&iacute;nh năng mạnh mẽ</strong></h3>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Vi xử l&yacute; M1 đến từ Apple với 8 nh&acirc;n cho CPU v&agrave; GPU mang đến tốc độ nhanh đến 2,8 lần, xử l&yacute; đồ họa nhanh hơn 5 lần thế hệ trước. T&iacute;ch hợp 16 nh&acirc;n Neurral Engine n&acirc;ng cao khả năng xử l&yacute; cho c&aacute;c t&aacute;c vụ Machine Learning nhanh hơn 15 lần.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>MacOS Big Sur mới nhất tr&ecirc;n <strong>Macbook Pro M1 2020</strong> gi&uacute;p tối ưu cho vi xử l&yacute; mới. Hệ điều h&agrave;nh mới n&acirc;ng cấp giao diện người d&ugrave;ng, thay đổi biểu tượng ứng dụng theo hướng tối giản, bổ sung Control Center, Notification Center để t&ugrave;y chỉnh nhanh một số thiết lập, quản l&yacute; th&ocirc;ng b&aacute;o đến.</p>\r\n\r\n<h3><strong>Thời lượng pin l&ecirc;n đến 20 giờ, quạt tản nhiệt thế hệ mới</strong></h3>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Nhờ sử dụng vi xử l&yacute; thiết kế ri&ecirc;ng n&ecirc;n Apple Macbook Pro 13 Touch Bar M1 đem đến cải tiến lớn về thời gian hoạt động. Chiếc m&aacute;y mang đến thời gian lướt web l&ecirc;n đến 17 giờ v&agrave; xem video li&ecirc;n tục 20 giờ đồng hồ.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Quạt tản nhiệt tr&ecirc;n Apple Macbook Pro 13 Touch Bar M1 l&agrave; điểm n&acirc;ng cấp mới so với thế hệ trước đ&oacute;. Trang bị quạt tản nhiệt hiệu suất cao được gọi l&agrave; Active Cooling, cho ph&eacute;p duy tr&igrave; hiệu suất hoạt động của vi xử l&yacute; gi&uacute;p m&aacute;y vận h&agrave;nh tốt nhất trong thời gian d&agrave;i.</p>\r\n</div>', '{\"Loại CPU\":\"8 nhân với 4 nhân hiệu năng cao và 4 nhân tiết kiệm điện\",\"Loại card đồ họa\":\"8 nhân GPU, 16 nhân Neural Engine\",\"Dung lượng RAM\":\"8GB\",\"Ổ cứng\":\"256GB SSD\",\"Kích thước màn hình\":\"13.3 inches\",\"Độ phân giải màn hình\":\"2560 x 1600 pixel (2K)\",\"Cổng giao tiếp\":\"2 cổng Thunderbolt \\/ USB 4 ports\",\"Hệ điều hành\":\"macOS Big Sur\",\"Pin\":\"58.2-watt-hour lithium-polymer, 61W USB-C Power Adapter\",\"Kích thước\":\"1.56 cm x 30.41 cm x 21.24 cm\",\"Trọng lượng\":\"1.4 kg\",\"Công nghệ màn hình\":\"Độ sáng 500 nits Hỗ trợ dải màu P3 True-Tone\",\"Màn hình cảm ứng\":\"Không\",\"Chất liệu tấm nền\":\"Tấm nền IPS\",\"Độ phân giải\":\"Retina\",\"Wi-Fi\":\"802.11ax Wi-Fi 6\",\"Bluetooth\":\"Bluetooth 5.0\",\"Khe đọc thẻ nhớ\":\"Không\",\"Tính năng đặc biệt\":\"Ổ cứng SSD, Bảo mật vân tay, Wi-Fi 6, Viền màn hình siêu mỏng\"}', 1, 2, 5, 1, '2021-07-15 16:19:58', '2021-07-17 11:54:42', NULL);
INSERT INTO `products` VALUES (558, 'Apple Mac mini M1 256GB 2020', 'apple-mac-mini-m1-256gb-2020', 60, 15000000, 19990000, 0, '<div class=\"blog-content\">\r\n<h2><strong>Apple Mac Mini 2020 M1 256GB Ch&iacute;nh H&atilde;ng (MGNR3) &ndash; D&ograve;ng Mac nhỏ gọn v&agrave; mạnh mẽ&nbsp;</strong></h2>\r\n\r\n<p>Nếu l&agrave; người th&iacute;ch một chiếc m&aacute;y t&iacute;nh nhỏ gọn, đơn giản nhưng c&oacute; hiệu năng phải thật mạnh mẽ để hỗ trợ bạn d&ugrave; l&agrave; c&ocirc;ng việc hay trong giải tr&iacute; th&igrave; d&ograve;ng m&aacute;y t&iacute;nh&nbsp;<strong>Apple <a href=\"https://cellphones.com.vn/laptop/mac/mini.html\" target=\"_self\" title=\"Mac Mini chính hãng\">Mac Mini</a> M1 256GB 2020&nbsp;</strong>sẽ l&agrave; người bạn đồng h&agrave;nh l&yacute; tưởng của bạn. H&atilde;y c&ugrave;ng điểm qua những điểm nổi bật của chiếc m&aacute;y t&iacute;nh Mac nhỏ gọn nhất vừa được ra mắt của nh&agrave; T&aacute;o nh&eacute;.</p>\r\n\r\n<h3><strong>K&iacute;ch thước nhỏ gọn v&agrave; tối giản, ho&agrave;n thiện từ hợp kim cao cấp</strong></h3>\r\n\r\n<p>Mac Mini M1 2020 256GB&nbsp;sở hữu k&iacute;ch thước v&ocirc; c&ugrave;ng ấn tượng với 19.7 cm cho cả chiều rộng v&agrave; chiều ngang, c&acirc;n nặng chỉ 1.2 kg. Nhờ thiết kế nhỏ gọn n&agrave;y m&agrave; khi hoạt động m&aacute;y giảm lượng điện năng ti&ecirc;u thụ l&ecirc;n đến 60% so với thế hệ trước đ&oacute;.</p>\r\n\r\n<p><img alt=\"Kích thước nhỏ gọn và tối giản, hoàn thiện từ hợp kim cao cấp\" class=\"cpslazy loaded\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/laptop/mac-mini-m1-256gb-2020.jpg\" style=\"height:600px; width:100%\" /></p>\r\n\r\n<p>B&ecirc;n ngo&agrave;i của chiếc Mac mini m1 được chế t&aacute;c ho&agrave;n to&agrave;n 100% từ hợp kim nh&ocirc;m được t&aacute;i chế, vừa gi&uacute;p tiết giảm chi ph&iacute; sản xuất, vừa bảo vệ m&ocirc;i trường.</p>\r\n\r\n<h3><strong>Vi xử l&yacute; M1 mạnh mẽ với 8 nh&acirc;n CPU v&agrave; GPU, 16 nh&acirc;n Neural Engine</strong></h3>\r\n\r\n<p>Apple Mac Mini M1 sở hữu vi xử l&yacute; M1 mới nhất được ch&iacute;nh Apple thiết kế, mang đến hiệu năng vượt trội với 8 nh&acirc;n CPU mang đến tốc độ xử l&yacute; gấp 3 lần v&agrave; 8 nh&acirc;n GPU cho khả năng xử l&yacute; độ họa gấp 8 lần thế hệ trước đ&oacute;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Vi xử l&yacute; M1 c&ograve;n mang đến 16 nh&acirc;n Neural Engine v&agrave; đầy đủ c&aacute;c c&ocirc;ng nghệ m&aacute;y học ti&ecirc;n tiến cho khả năng xử l&yacute; c&aacute;c t&aacute;c vụ về t&iacute;nh to&aacute;n, ph&acirc;n t&iacute;ch video, nhận dạng giọng n&oacute;i, v&agrave; nhiều hơn nữa.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3><strong>8GB RAM, SSD c&ocirc;ng nghệ lưu trữ Flash tốc độ truy xuất l&ecirc;n đến 3,4GB/s</strong></h3>\r\n\r\n<p>Trang bị bộ nhớ RAM 8GB&nbsp;c&ugrave;ng SSD 256GB cho khả năng lưu trữ mạnh mẽ v&agrave; xử l&yacute; đa t&aacute;c vụ giờ đ&acirc;y l&agrave; v&ocirc; c&ugrave;ng dễ d&agrave;ng tr&ecirc;n Apple Mac Mini M1 2020.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Với M1 sử dụng kiến tr&uacute;c bộ nhớ hợp nhất gi&uacute;p tận dụng băng th&ocirc;ng tốc độ cao từ RAM v&agrave; CPU hiệu quả. Nhờ vậy cho khả năng xử l&yacute; video nhanh hơn đến 3,9 lần v&agrave; 7,1 lần cho xử l&yacute; h&igrave;nh ảnh tr&ecirc;n chiếc Mac Mini n&agrave;y.</p>\r\n\r\n<h3><strong>Bảo mật th&ocirc;ng tin với vi xử l&yacute; Secure Enclave, m&atilde; h&oacute;a dữ liệu AES v&agrave; định danh với Apple ID</strong></h3>\r\n\r\n<p>Bảo mật dữ liệu v&agrave; th&ocirc;ng tin c&aacute; nh&acirc;n người d&ugrave;ng l&agrave; ti&ecirc;u ch&iacute; m&agrave; người d&ugrave;ng hay lựa chọn khi mua c&aacute;c sản&nbsp;phẩm của Apple. Với Mac Mini M1 điều đ&oacute; cũng kh&ocirc;ng l&agrave; ngoại lệ.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Sử dụng một bộ vi xử l&yacute; ri&ecirc;ng cho bảo mật mang t&ecirc;n Secure Enclave, cơ chế m&atilde; h&oacute;a dữ liệu tr&ecirc;n ổ cứng với AES v&agrave; định danh t&agrave;i khoản với Apple ID mang đến khả năng bảo mật gần như ho&agrave;n hảo.</p>\r\n\r\n<h3><strong>Cổng Thunderbot / USB 4 truyền tải dữ liệu l&ecirc;n đến 40GB/s, HDMI 2.0 truyền m&agrave;n h&igrave;nh độ ph&acirc;n giải cao</strong></h3>\r\n\r\n<p>L&agrave; chiếc mac mini với hiệu năng mạnh mẽ, th&igrave; đi k&egrave;m n&oacute; l&agrave; t&iacute;ch hợp cổng kết nối hiện đại nhất. Với 2 cổng Thunderbolt / USB thế hệ thứ 4 cho ph&eacute;p người d&ugrave;ng truyền dữ liệu tốc độ si&ecirc;u cao l&ecirc;n đến 40GB/s/.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>C&ugrave;ng với đ&oacute;, người d&ugrave;ng c&oacute; thể gắn th&ecirc;m m&agrave;n h&igrave;nh với độ ph&acirc;n giải l&ecirc;n đến 6K si&ecirc;u sắc n&eacute;t v&agrave; mượt m&agrave; nhờ cổng kết nối HDMI 2.0 mới nhất với mac mini 2020 n&agrave;y.</p>\r\n\r\n<h3><strong>Kết nối internet tốc độ cao với Wi-Fi 6 v&agrave; cổng Ethernet Gigabit</strong></h3>\r\n\r\n<p>Nếu l&agrave; người d&ugrave;ng y&ecirc;u th&iacute;ch c&aacute;c nội dung giải tr&iacute; internet với chất lượng tốt nhất, th&igrave; Mac Mini M1 2020 sẽ kh&ocirc;ng khiến bạn phải thất vọng với Wi-Fi 6 mới nhất cho tốc độ download v&agrave; upload l&ecirc;n đến 1,2GB/s.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>C&ograve;n nếu bạn l&agrave; một game thủ v&agrave; muốn trải nghiệm một đường truyền với tốc độ cao, ổ định v&agrave; &iacute;t giật lag th&igrave; cổng Ethernet Gigabit với tốc độ 1GB/s sẽ gi&uacute;p trải nghiệm giải tr&iacute; của bạn v&ocirc; c&ugrave;ng ho&agrave;n hảo.</p>\r\n\r\n<h3><strong>macOS BigSur tận dụng tối đa sức mạnh phần cứng</strong></h3>\r\n\r\n<p>Một phần cứng tốt phải đi k&egrave;m với một phần mềm chất lượng. Hệ điều h&agrave;nh macOS BigSur tận dụng tối đa sức mạnh phần cứng của vi xử l&yacute; M1. Mang đến trải nghiệm người d&ugrave;ng tuyệt vời d&ugrave; l&agrave; trong c&ocirc;ng việc hay giải tr&iacute;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Thay đổi giao diện người d&ugrave;ng mới, bổ sung th&ecirc;m thanh menu v&agrave; trung t&acirc;m điều khiển. Thay đổi lại thiết kế biểu tượng của trang t&iacute;nh, hệ thống &acirc;m thanh v&agrave; trung t&acirc;m th&ocirc;ng b&aacute;o tất cả đều chờ bạn&nbsp;tr&ecirc;n chiếc Apple Mac Mini M1 n&agrave;y.</p>\r\n</div>', '{\"Loại CPU\":\"8 nhân với 4 nhân hiệu năng cao và 4 nhân tiết kiệm điện\",\"Loại card đồ họa\":\"8 nhân GPU, 16 nhân Neural Engine\",\"Dung lượng RAM\":\"8GB\",\"Ổ cứng\":\"256GB SSD\",\"Kích thước màn hình\":\"13.3 inches\",\"Độ phân giải màn hình\":\"2560 x 1600 pixel (2K)\",\"Cổng giao tiếp\":\"2 cổng Thunderbolt \\/ USB 4 ports\",\"Hệ điều hành\":\"macOS Big Sur\",\"Pin\":\"58.2-watt-hour lithium-polymer, 61W USB-C Power Adapter\",\"Kích thước\":\"1.56 cm x 30.41 cm x 21.24 cm\",\"Trọng lượng\":\"1.4 kg\",\"Công nghệ màn hình\":\"Độ sáng 500 nits Hỗ trợ dải màu P3 True-Tone\",\"Màn hình cảm ứng\":\"Không\",\"Chất liệu tấm nền\":\"Tấm nền IPS\",\"Độ phân giải\":\"Retina\",\"Wi-Fi\":\"802.11ax Wi-Fi 6\",\"Bluetooth\":\"Bluetooth 5.0\",\"Khe đọc thẻ nhớ\":\"Không\",\"Tính năng đặc biệt\":\"Ổ cứng SSD, Bảo mật vân tay, Wi-Fi 6, Viền màn hình siêu mỏng\"}', 1, 2, 5, 1, '2021-07-15 16:25:48', '2021-07-17 17:46:50', NULL);
INSERT INTO `products` VALUES (559, 'Apple MacBook Air M1 16GB 256GB 2020', 'apple-macbook-air-m1-16gb-256gb-2020', 60, 21000000, 32990000, 0, '<p><strong><a href=\"https://cellphones.com.vn/laptop/mac/macbook-air.html\" target=\"_blank\">Macbook Air</a>&nbsp;M1 2020&nbsp;</strong>phi&ecirc;n bản RAM 16GB v&agrave; dung lượng lưu trữ 256GB được trang bị m&agrave;n h&igrave;nh 13.3 inch độ ph&acirc;n giải&nbsp;2560 x 1600 pixels,&nbsp;chip xử l&yacute; Apple M1 8 nh&acirc;n, bao gồm 4 nh&acirc;n hiệu suất cao v&agrave; 4 nh&acirc;n hiệu suất thấp mang đến sức mạnh vượt trội hơn đến 98% so với c&aacute;c laptop Windows v&agrave; hiệu năng vượt trội hơn so với c&aacute;c phi&ecirc;n bản Air sử dụng chip Intel.</p>\r\n\r\n<p>T&igrave;m hiểu th&ecirc;m&nbsp;<a href=\"https://cellphones.com.vn/macbook-air-2020-m1.html\" target=\"_blank\">Apple MacBook Air M1 256GB 2020</a>&nbsp;mang đến một thiết kế tinh tế, sang trọng c&ugrave;ng hiệu năng si&ecirc;u khủng, si&ecirc;u ấn tượng cho người d&ugrave;ng.</p>', '{\"Loại CPU\":\"8 nhân với 4 nhân hiệu năng cao và 4 nhân tiết kiệm điện\",\"Loại card đồ họa\":\"8 nhân GPU, 16 nhân Neural Engine\",\"Dung lượng RAM\":\"8GB\",\"Ổ cứng\":\"256GB SSD\",\"Kích thước màn hình\":\"13.3 inches\",\"Độ phân giải màn hình\":\"2560 x 1600 pixel (2K)\",\"Cổng giao tiếp\":\"2 cổng Thunderbolt \\/ USB 4 ports\",\"Hệ điều hành\":\"macOS Big Sur\",\"Pin\":\"58.2-watt-hour lithium-polymer, 61W USB-C Power Adapter\",\"Kích thước\":\"1.56 cm x 30.41 cm x 21.24 cm\",\"Trọng lượng\":\"1.4 kg\",\"Công nghệ màn hình\":\"Độ sáng 500 nits Hỗ trợ dải màu P3 True-Tone\",\"Màn hình cảm ứng\":\"Không\",\"Chất liệu tấm nền\":\"Tấm nền IPS\",\"Độ phân giải\":\"Retina\",\"Wi-Fi\":\"802.11ax Wi-Fi 6\",\"Bluetooth\":\"Bluetooth 5.0\",\"Khe đọc thẻ nhớ\":\"Không\",\"Tính năng đặc biệt\":\"Ổ cứng SSD, Bảo mật vân tay, Wi-Fi 6, Viền màn hình siêu mỏng\"}', 1, 2, 5, 1, '2021-07-15 16:28:41', '2021-07-17 17:46:50', NULL);
INSERT INTO `products` VALUES (560, 'iMac 24 2021 M1 7GPU 8GB 256GB', 'imac-24-2021-m1-7gpu-8gb-256gb', 60, 25000000, 34990000, 0, '<div class=\"blog-content\">\r\n<h2 style=\"text-align:justify\"><strong>iMac 2021 trải nghiệm ấn tượng với m&agrave;n h&igrave;nh 4.5K</strong></h2>\r\n\r\n<p style=\"text-align:justify\">Mac l&agrave; thiết bị được t&iacute;ch hợp m&agrave;n h&igrave;nh với hệ thống CPU. Với thiết kế tinh tế, hoạt động mạnh mẽ, iMac sẽ gi&uacute;p kh&ocirc;ng gian l&agrave;m việc của bạn th&ecirc;m phần sang trọng cũng như tiết kiệm được tối đa khoảng trống. L&agrave; thiết kế mới nhất của Apple, <strong><a href=\"https://cellphones.com.vn/laptop/mac/imac.html\" target=\"_self\" title=\"iMac chính hãng\">iMac</a> 2021</strong> chắc chắn sẽ mang đến cho người d&ugrave;ng những trải nghiệm ấn tượng kh&ocirc;ng chỉ về thiết kế m&agrave; c&ograve;n cả về hiệu năng.</p>\r\n\r\n<h3 style=\"text-align:justify\"><strong>Thiết kế si&ecirc;u mỏng, 7 t&ugrave;y chọn m&agrave;u sắc</strong></h3>\r\n\r\n<p style=\"text-align:justify\">So với c&aacute;c phi&ecirc;n bản trước đ&oacute;, iMac 2021 được thiết kế n&acirc;ng cấp mang đến sự bất ngờ cho c&aacute;c t&iacute;n đồ nh&agrave; IFan.</p>\r\n\r\n<p style=\"text-align:justify\">Chiếc iMac mới chỉ mỏng 11.4mm, n&oacute; c&oacute; độ mỏng gấp đ&ocirc;i so với một chiếc iPad Pro 12.9-inch. Nh&igrave;n tổng thể ch&uacute;ng tr&ocirc;ng giống như một chiếc iPad ph&oacute;ng to v&agrave; được k&egrave;m theo đ&oacute; l&agrave; một chiếc ch&acirc;n đế. iMac mới n&agrave;y nhỏ hơn tới 50% so với thế hệ tiền nhiệm nhưng c&oacute; khả năng hiển thị rộng hơn do viền m&agrave;n h&igrave;nh được tinh chỉnh tối đa. Đ&acirc;y sẽ l&agrave; một sự lựa chọn hợp l&yacute; d&agrave;nh cho những ai muốn sở hữu một chiếc b&agrave;n l&agrave;m việc gọn g&agrave;ng v&agrave; tối giản.</p>\r\n\r\n<p style=\"text-align:justify\"><img alt=\"Thiết kế siêu mỏng, 7 tùy chọn màu sắc\" class=\"cpslazy loaded\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/laptop/iMac-2021-1.jpg\" style=\"height:600px; width:100%\" /></p>\r\n\r\n<p style=\"text-align:justify\">iMac 2021 được l&agrave;m từ chất liệu nh&ocirc;m t&aacute;i chế 100%. Với 7 tuỳ chọn m&agrave;u sắc bao gồm: xanh, v&agrave;ng, cam, đỏ, t&iacute;m, xanh dương v&agrave; m&agrave;u bạc sẽ mang đến cho người d&ugrave;ng lựa chọn y&ecirc;u th&iacute;ch.</p>\r\n\r\n<h3 style=\"text-align:justify\"><strong>M&agrave;n h&igrave;nh 24 inch, n&acirc;ng cấp ấn tượng</strong></h3>\r\n\r\n<p style=\"text-align:justify\">Kh&ocirc;ng chỉ về thiết kế, m&agrave;n h&igrave;nh iMac 2021 cũng được n&acirc;ng cấp kh&aacute; ấn tượng. Với k&iacute;ch thước 24 inch v&agrave; độ ph&acirc;n giải 4.5K kết hợp với viền m&agrave;n h&igrave;nh cực mỏng từ đ&oacute; mang đến khả năng hiển thị rộng hơn.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">M&agrave;n h&igrave;nh tr&ecirc;n thiết bị c&ograve;n mang đến chất lượng kh&aacute; tốt với khả năng hỗ trợ dải m&agrave;u DCI-P3 chứa hơn 1 tỷ m&agrave;u sắc kh&aacute;c nhau. Độ s&aacute;ng m&agrave;n h&igrave;nh l&ecirc;n tới 500 nits v&agrave; t&iacute;ch hợp c&ocirc;ng nghệ True Tone gi&uacute;p h&igrave;nh ảnh v&ocirc; c&ugrave;ng sống động m&agrave; kh&ocirc;ng k&eacute;m ch&acirc;n thực. B&ecirc;n cạnh đ&oacute;, một lớp chống l&oacute;a tr&ecirc;n m&agrave;n h&igrave;nh gi&uacute;p người d&ugrave;ng c&oacute; trải nghiệm thị gi&aacute;c tốt nhất.</p>\r\n\r\n<h3 style=\"text-align:justify\"><strong>Chip M1 v&ocirc; c&ugrave;ng mạnh mẽ</strong></h3>\r\n\r\n<p style=\"text-align:justify\">Chiếc iMac 2021 của Apple được trang bị con chip Apple M1 mang đến hiệu năng v&ocirc; c&ugrave;ng mạnh mẽ. B&ecirc;n cạnh đ&oacute;, khả năng ti&ecirc;u thụ điện năng hiệu quả hơn rất nhiều lần so với thế hệ chip Intel Comet Lake được sử dụng tr&ecirc;n d&ograve;ng iMac 27-inch 2020 trước đ&oacute;.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">Đặc biệt, con chip M1 n&agrave;y tr&ecirc;n iMac gi&uacute;p cải thiện đến 85% hiệu năng CPU, cải thiện gấp đ&ocirc;i hiệu năng GPU v&agrave; tối ưu cho con chip M1. Đặc biệt c&aacute;c ứng dụng đồ hoạ được trang bị gi&uacute;p cho iMac M1 c&oacute; khả năng stream tối đa đến 5 luồng video 4K hoặc l&agrave; 1 luồng video 8K trong Final Cut Pro m&agrave; kh&ocirc;ng xảy ra hiện tượng bị drop frame.</p>\r\n\r\n<h3 style=\"text-align:justify\"><strong>Webcam, loa, mic,... cải thiện r&otilde; rệt</strong></h3>\r\n\r\n<p style=\"text-align:justify\">Ngo&agrave;i những yếu tố tr&ecirc;n th&igrave; webcam, loa, mic,... tr&ecirc;n phi&ecirc;n bản mới nhất của iMac n&agrave;y cũng được cải thiện đ&aacute;ng kể. Webcam của m&aacute;y c&oacute; độ ph&acirc;n giải 1080p kết hợp với bộ xử l&yacute; h&igrave;nh ảnh trong con chip M1 mang đến h&igrave;nh ảnh chất lượng hơn, quay video ấn tượng hơn v&agrave; d&agrave;i tương phản động tốt hơn.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">B&ecirc;n cạnh webcam th&igrave; hệ thống mic thu &acirc;m ấn tượng mang chất lượng ph&ograve;ng thu bao gồm 3 microphone ri&ecirc;ng biệt. Ch&uacute;ng c&oacute; khả năng khử tiếng ồn m&ocirc;i trường v&agrave; cải thiện chất lượng thu &acirc;m.</p>\r\n\r\n<h3 style=\"text-align:justify\"><strong>T&iacute;ch hợp Touch ID, khả năng kết nối đa dạng</strong></h3>\r\n\r\n<p style=\"text-align:justify\">iMac 2021 được Apple t&iacute;ch hợp Touch ID tr&ecirc;n b&agrave;n ph&iacute;m Magic Keyboard mang đến độ bảo mật cao cũng như sự tiện lợi trong qu&aacute; tr&igrave;nh sử dụng. B&ecirc;n cạnh đ&oacute;, rất nhiều phụ kiện b&agrave;n ph&iacute;m như Magic Keyboard hay Magic Mouse hay l&agrave; Magic Trackpad với nhiều m&agrave;u sắc tương tự như d&ograve;ng iMac mới n&agrave;y để mang đến sự đồng bộ cho người d&ugrave;ng.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n</div>', '{\"Loại CPU\":\"8 nhân với 4 nhân hiệu năng cao và 4 nhân tiết kiệm điện\",\"Loại card đồ họa\":\"8 nhân GPU, 16 nhân Neural Engine\",\"Dung lượng RAM\":\"8GB\",\"Ổ cứng\":\"256GB SSD\",\"Kích thước màn hình\":\"13.3 inches\",\"Độ phân giải màn hình\":\"2560 x 1600 pixel (2K)\",\"Cổng giao tiếp\":\"2 cổng Thunderbolt \\/ USB 4 ports\",\"Hệ điều hành\":\"macOS Big Sur\",\"Pin\":\"58.2-watt-hour lithium-polymer, 61W USB-C Power Adapter\",\"Kích thước\":\"1.56 cm x 30.41 cm x 21.24 cm\",\"Trọng lượng\":\"1.4 kg\",\"Công nghệ màn hình\":\"Độ sáng 500 nits Hỗ trợ dải màu P3 True-Tone\",\"Màn hình cảm ứng\":\"Không\",\"Chất liệu tấm nền\":\"Tấm nền IPS\",\"Độ phân giải\":\"Retina\",\"Wi-Fi\":\"802.11ax Wi-Fi 6\",\"Bluetooth\":\"Bluetooth 5.0\",\"Khe đọc thẻ nhớ\":\"Không\",\"Tính năng đặc biệt\":\"Ổ cứng SSD, Bảo mật vân tay, Wi-Fi 6, Viền màn hình siêu mỏng\"}', 1, 2, 5, 1, '2021-07-15 16:39:51', '2021-07-17 17:46:50', NULL);
INSERT INTO `products` VALUES (561, 'Apple iMac 27 5K 2020 i7 3.8 8GB 512GB Radeon 5500XT', 'apple-imac-27-5k-2020-i7-38-8gb-512gb-radeon-5500xt', 60, 35000000, 56000000, 0, NULL, '{\"Loại CPU\":\"8 nhân với 4 nhân hiệu năng cao và 4 nhân tiết kiệm điện\",\"Loại card đồ họa\":\"8 nhân GPU, 16 nhân Neural Engine\",\"Dung lượng RAM\":\"8GB\",\"Ổ cứng\":\"256GB SSD\",\"Kích thước màn hình\":\"13.3 inches\",\"Độ phân giải màn hình\":\"2560 x 1600 pixel (2K)\",\"Cổng giao tiếp\":\"2 cổng Thunderbolt \\/ USB 4 ports\",\"Hệ điều hành\":\"macOS Big Sur\",\"Pin\":\"58.2-watt-hour lithium-polymer, 61W USB-C Power Adapter\",\"Kích thước\":\"1.56 cm x 30.41 cm x 21.24 cm\",\"Trọng lượng\":\"1.4 kg\",\"Công nghệ màn hình\":\"Độ sáng 500 nits Hỗ trợ dải màu P3 True-Tone\",\"Màn hình cảm ứng\":\"Không\",\"Chất liệu tấm nền\":\"Tấm nền IPS\",\"Độ phân giải\":\"Retina\",\"Wi-Fi\":\"802.11ax Wi-Fi 6\",\"Bluetooth\":\"Bluetooth 5.0\",\"Khe đọc thẻ nhớ\":\"Không\",\"Tính năng đặc biệt\":\"Ổ cứng SSD, Bảo mật vân tay, Wi-Fi 6, Viền màn hình siêu mỏng\"}', 1, 2, 5, 1, '2021-07-15 16:41:52', '2021-07-17 11:54:42', NULL);
INSERT INTO `products` VALUES (562, 'Laptop ASUS Gaming ROG Strix Scar G15 G533QR-HQ098T', 'laptop-asus-gaming-rog-strix-scar-g15-g533qr-hq098t', 60, 40000000, 59900000, 0, '<div class=\"blog-content\">\r\n<h2 style=\"text-align:justify\"><strong>Laptop ASUS Gaming ROG Strix Scar G15 G533QR-HQ098T đỉnh cao của laptop gaming</strong></h2>\r\n\r\n<p style=\"text-align:justify\">Asus nổi tiếng với d&ograve;ng laptop gaming chuy&ecirc;n dụng d&agrave;nh ri&ecirc;ng cho c&aacute;c game thủ. Với thiết kế hầm hố, hiệu năng ấn tượng khiến người d&ugrave;ng ch&igrave;m đắm trong c&aacute;c tựa game. Đặc biệt với <strong><a href=\"https://cellphones.com.vn/laptop/asus/gaming.html\" target=\"_blank\" title=\"Laptop Asus Gaming\">laptop Asus Gaming</a> ROG Strix Scar G15 G533QR-HQ098T</strong> sẽ gi&uacute;p bạn c&oacute; những trải nghiệm cực kỳ kh&oacute; qu&ecirc;n.</p>\r\n\r\n<h3 style=\"text-align:justify\"><strong>Chiến mọi tựa game với CPU AMD Ryzen 9 5900HX</strong></h3>\r\n\r\n<p style=\"text-align:justify\">Laptop Asus Gaming ROG Strix Scar G15 G533QR-HQ098T l&agrave; đỉnh cao của laptop gaming với trang bị CPU AMD Ryzen 9 5900HX. C&ugrave;ng với đ&oacute; l&agrave; GPU RTX 3070 hỗ trợ ROG Boost l&ecirc;n tới 1540 MHz 115W ở chế độ Turbo sẽ cho ph&eacute;p người chơi c&oacute; những m&agrave;n gameplay ấn tượng.</p>\r\n\r\n<p style=\"text-align:justify\"><img alt=\"Laptop ASUS Gaming ROG Strix Scar G15 G533QR-HQ098T\" class=\"cpslazy loaded\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/laptop/Laptop-asus-gaming-rog-strix-scar-g15-g533qr-hq098t-r9-1_1_.jpg\" style=\"height:600px; width:100%\" /></p>\r\n\r\n<p style=\"text-align:justify\">B&ecirc;n cạnh chip cực khủng l&agrave; RAM 16GB kết hợp với ổ cứng SSD 1TB PCIE kh&ocirc;ng chỉ gi&uacute;p những t&aacute;c vụ đa nhiệm h&agrave;ng ng&agrave;y đều trở n&ecirc;n nhanh hơn v&agrave; mượt m&agrave; hơn m&agrave; bạn c&oacute; thể thoải m&aacute;i sử dụng c&aacute;c ứng dụng đồ họa chuy&ecirc;n nghiệp m&agrave; kh&ocirc;ng hề lo bị giật hay lag.</p>\r\n\r\n<p style=\"text-align:justify\"><em>&gt;&gt;&gt; Kh&aacute;m ph&aacute; th&ecirc;m chi tiết&nbsp;<a href=\"https://cellphones.com.vn/laptop-asus-rog-zephyrus-duo-15-se-gx551qr.html\" target=\"_blank\">Laptop ASUS Gaming ROG Zephyrus Duo 15 SE GX551QR-HF080T</a> mang đến trải nghiệm chơi game mạnh mẽ, linh hoạt</em></p>\r\n\r\n<h3 style=\"text-align:justify\"><strong>M&agrave;n h&igrave;nh 15.6 inch, thiết kế ấn tượng</strong></h3>\r\n\r\n<p style=\"text-align:justify\">Laptop Asus Gaming ROG Strix Scar G15 G533QR-HQ098T được trang bị m&agrave;n h&igrave;nh 15.6 inch, tần số qu&eacute;t 165hz c&ugrave;ng với RangeBooster Wi-Fi mang đến tốc độ phản ứng nhanh nhất, gi&uacute;p người chơi kh&ocirc;ng bỏ lỡ bất k&igrave; nhịp n&agrave;o trong game.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">Vỏ ngo&agrave;i của m&aacute;y bằng kim loại, những đường ph&acirc;y xước ấn tượng c&ugrave;ng led &aacute;nh s&aacute;ng Aura Sync bao quanh th&acirc;n m&aacute;y v&agrave; hiệu ứng t&ugrave;y chỉnh gi&uacute;p người d&ugrave;ng c&oacute; thể dễ d&agrave;ng điều chỉnh theo &yacute; muốn. Đặc biệt, viền m&aacute;y mỏng mang đến sự gọn nhẹ v&agrave; thuận tiện khi di chuyển.</p>\r\n\r\n<h3 style=\"text-align:justify\"><strong>B&agrave;n ph&iacute;m ch&iacute;nh x&aacute;c, bền bỉ, cổng kết nối đa dạng</strong></h3>\r\n\r\n<p style=\"text-align:justify\">B&agrave;n ph&iacute;m của m&aacute;y kh&aacute; th&acirc;n thiện với người d&ugrave;ng, c&aacute;c ph&iacute;m chức năng dễ d&agrave;ng nhận biết. Ph&iacute;m n&oacute;ng chuy&ecirc;n dụng gi&uacute;p tiết kiệm tối đa thời gian thao t&aacute;c. Hơn nữa, trang bị loa hiện đại tr&ecirc;n m&aacute;y gi&uacute;p game thủ trải nghiệm &acirc;m thanh cực chất.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">Laptop Asus Gaming ROG Strix Scar G15 G533QR-HQ098T c&oacute; khả năng kết nối đa dạng. Sở hữu đầy đủ c&aacute;c cổng kết nối gi&uacute;p người d&ugrave;ng dễ d&agrave;ng li&ecirc;n kết với mọi thiết bị hoặc mang đến trải nghiệm lớn hơn khi kết nối HDMI với tivi.</p>\r\n</div>', '{\"Loại CPU\":\"AMD Ryzen 9 5900HX 3.3GHz up to 4.6GHz 16MB\",\"Loại card đồ họa\":\"NVIDIA GeForce RTX 3070 8GB GDDR6\",\"Dung lượng RAM\":\"16GB\",\"Loại RAM\":\"DDR4 3200Mhz, 2 khe cắm, hỗ trợ tối đa 32GB\",\"Ổ cứng\":\"1TB SSD PCIE G3X4 (2 slot)\",\"Kích thước màn hình\":\"15.6 inches\",\"Độ phân giải màn hình\":\"2560x1440 (Quad HD)\",\"Cổng giao tiếp\":\"1x Type C USB 3.2 Gen 2, 3x USB 3.2 Gen 1 Type-A, 1x HDMI 2.0b, 1x 3.5mm Audio Jack, 1x RJ45 LAN Jack, 1x Keystone\",\"Hệ điều hành\":\"Windows 10 Home\",\"Pin\":\"4 Cell 90WHrs\",\"Kích thước\":\"35.4 x 25.9 x 2.26 cm\",\"Trọng lượng\":\"2.3 kg\",\"Công nghệ màn hình\":\"Chất lượng màn hính NanoEdge, Độ bao phủ màu DCI-P3:100.00%\",\"Màn hình cảm ứng\":\"Không\",\"Chất liệu tấm nền\":\"Tấm nền IPS\",\"Độ phân giải\":\"2K (Quad HD)\",\"Wi-Fi\":\"Wi-Fi 6 802.11ax (2x2)\",\"Bluetooth\":\"v5.1\",\"Khe đọc thẻ nhớ\":\"Có\",\"Tính năng đặc biệt\":\"Ổ cứng SSD, Wi-Fi 6\"}', 1, 2, 2, 1, '2021-07-15 17:08:59', '2021-07-17 11:54:42', NULL);
INSERT INTO `products` VALUES (563, 'Laptop ASUS Zenbook Duo 14 UX482EG', 'laptop-asus-zenbook-duo-14-ux482eg', 60, 36000000, 40900000, 0, '<div class=\"blog-content\">\r\n<h2 style=\"text-align:justify\"><strong>Laptop ASUS ZenBook Duo 14 UX482EG - Làm vi&ecirc;̣c đ&acirc;̀y phong cách và chuy&ecirc;n nghi&ecirc;̣p</strong></h2>\r\n\r\n<p style=\"text-align:justify\"><em>Mang thi&ecirc;́t k&ecirc;́ mỏng nhẹ, c&ocirc;ng ngh&ecirc;̣ màn hình phụ ScreenPad&trade; Plus và chứa hi&ecirc;̣u năng mạnh mẽ nhờ vi xử lý Intel Core th&ecirc;́ h&ecirc;̣ 11, </em><em><strong>laptop ASUS ZenBook Duo 14 UX482EG</strong> mang đ&ecirc;́n cho bạn trải nghi&ecirc;̣m làm vi&ecirc;̣c tr&ecirc;n laptop hi&ecirc;̣n đại nh&acirc;́t, chuy&ecirc;n nghi&ecirc;̣p và đ&acirc;̀y phong cách.</em></p>\r\n\r\n<h3 style=\"text-align:justify\"><strong>Thi&ecirc;́t k&ecirc;́ màn hình kép ScreenPad&trade; Plus với đ&ocirc;̣ ph&acirc;n giải Full HD sắc nét</strong></h3>\r\n\r\n<p style=\"text-align:justify\"><strong><a href=\"https://cellphones.com.vn/laptop/asus/zenbook.html\" target=\"_blank\">Laptop ASUS ZenBook</a> Duo 14 UX482EG</strong> mang đ&ecirc;́n cho giới làm vi&ecirc;̣c laptop m&ocirc;̣t cách xử lý c&ocirc;ng vi&ecirc;̣c đ&acirc;̀y mới mẻ, hi&ecirc;̣n đại và đa chức năng - màn hình kép ScreenPad&trade; Plus. Ngoài màn hình chính 14 inch đ&ocirc;̣ ph&acirc;n giải Full HD, ASUS ZenBook Duo 14 UX482EG còn có th&ecirc;m m&ocirc;̣t màn hình cảm ứng phụ đa chức năng với khả năng nghi&ecirc;ng 7 đ&ocirc;̣, giúp bạn quan sát rõ mọi chi ti&ecirc;́t hi&ecirc;̉n thị mà kh&ocirc;ng bị chói sáng.</p>\r\n\r\n<p style=\"text-align:justify\">Cả hai màn hình chính l&acirc;̃n phụ đ&ecirc;̀u được thi&ecirc;́t k&ecirc;́ theo phong cách c&ocirc;ng thái học đ&ocirc;̀ng đ&ecirc;̀u giữa các cạnh. Ph&acirc;̀n vi&ecirc;̀n màn hình được thu hẹp t&ocirc;́i đa, giúp hi&ecirc;̉n thị hình ảnh ch&acirc;n thực. Ngoài ra, cả hai màn hình đ&ecirc;̀u đạt chứng nh&acirc;̣n TUV Rheinland giảm thi&ecirc;̉u ánh sáng xanh, giúp bảo v&ecirc;̣ mắt bạn kh&ocirc;ng bị mỏi khi quan sát l&acirc;u dài.</p>\r\n\r\n<p style=\"text-align:justify\"><img alt=\"Thiết kế màn hình kép ScreenPad™ Plus với độ phân giải Full HD sắc nét\" class=\"cpslazy loaded\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/laptop/laptop-asus-zenbook-duo-ux482eg-i5-1135g7-8gb-512gb-14-fhd-xanh-1.jpg\" style=\"height:600px; width:100%\" /></p>\r\n\r\n<p style=\"text-align:justify\">ASUS ZenBook Duo 14 UX482EG là m&acirc;̃u laptop mà bạn có th&ecirc;̉ thoải mái mang theo b&ecirc;n mình b&acirc;́t kỳ lúc nào bởi thi&ecirc;́t k&ecirc;́ mỏng, nhẹ, màu xanh đẹp mắt và thời lượng pin t&acirc;̣n 17 giờ li&ecirc;̀n. B&ecirc;n trong ZenBook Duo 14 UX482EG còn được trang bị h&ecirc;̣ th&ocirc;́ng tản nhi&ecirc;̣t quạt kép đ&ocirc;̣c quy&ecirc;̀n của ASUS, giúp tăng lượng khí tản ra ngoài đ&ecirc;́n 49% và giữ cho ph&acirc;̀n cứng b&ecirc;n trong v&acirc;̣n hành &ecirc;m ái cho mọi c&ocirc;ng vi&ecirc;̣c bạn thực hi&ecirc;̣n.</p>\r\n\r\n<p style=\"text-align:justify\"><em>&gt;&gt;&gt; C&ugrave;ng t&igrave;m hiểu th&ecirc;m th&ocirc;ng tin mẫu&nbsp;<a href=\"https://cellphones.com.vn/laptop-asus-zenbook-14-ux425ea.html\" target=\"_blank\">Laptop ASUS ZenBook 14 UX425EA</a>&nbsp;&nbsp;</em></p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<h3 style=\"text-align:justify\"><strong>Vi xử lý Intel Core i5-1135G7 cùng đ&ocirc;̀ họa GeForce MX450 2GB v&acirc;̣n hành mạnh mẽ</strong></h3>\r\n\r\n<p style=\"text-align:justify\">Laptop ASUS ZenBook Duo 14 UX482EG được trang bị b&ocirc;̣ CPU Intel Core i5-1135G7 - con chip thu&ocirc;̣c th&ecirc;́ h&ecirc;̣ 11 ti&ecirc;n ti&ecirc;́n của Intel. Với 4 nh&acirc;n, 8 lu&ocirc;̀ng cùng t&acirc;̀n s&ocirc;́ dao đ&ocirc;̣ng 2.4 GHz ~ 4.2 GHz, b&ocirc;̣ CPU này sẽ làm cho t&acirc;́t cả c&ocirc;ng vi&ecirc;̣c đa nhi&ecirc;̣m, dù thực hi&ecirc;̣n tr&ecirc;n m&ocirc;̣t hay cả hai màn hình, cũng đ&ecirc;̀u trở n&ecirc;n mượt mà và nhanh chóng.</p>\r\n\r\n<p style=\"text-align:justify\">Với các c&ocirc;ng vi&ecirc;̣c đ&ocirc;̀ họa nặng, ASUS ZenBook Duo 14 UX482EG đáp ứng trọn qua card đ&ocirc;̀ họa NVIDIA GeForce MX450 2GB. Cho dù bạn là m&ocirc;̣t nhà sáng tạo n&ocirc;̣i dung, nhà thi&ecirc;́t k&ecirc;́ đ&ocirc;̀ họa, hoặc th&acirc;̣m chí là game thủ, ZenBook Duo 14 UX482EG sẽ đáp ứng trọn với b&ocirc;̣ đ&ocirc;i Intel Core i5-1135G7 và GeForce MX450.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">Đ&ecirc;̉ đáp ứng c&ocirc;ng vi&ecirc;̣c trình chi&ecirc;́u cho những bu&ocirc;̉i h&ocirc;̣i họp, ASUS ZenBook Duo 14 UX482EG còn được trang bị các phương thức k&ecirc;́t n&ocirc;́i t&ocirc;́c đ&ocirc;̣ cao như USB-C Thunderbolt 4, h&ocirc;̃ trợ cả sạc nhanh Power Delivery và chuy&ecirc;̉n đ&ocirc;̉i hình ảnh DisplayPort với ch&acirc;́t lượng l&ecirc;n đ&ecirc;́n 8K.</p>\r\n\r\n<p style=\"text-align:justify\">ZenBook Duo 14 UX482EG còn tương thích với WiFi 6, tích hợp cùng c&ocirc;ng ngh&ecirc;̣ ASUS WiFi Master Premium sẽ mang đ&ecirc;́n t&ocirc;́c đ&ocirc;̣ k&ecirc;́t n&ocirc;́i internet ch&acirc;́t lượng cao cho mọi nhu c&acirc;̀u chia sẻ dữ li&ecirc;̣u. B&ecirc;n trong máy còn có RAM 8 GB và &ocirc;̉ cứng SSD PCIe 512 GB làm vi&ecirc;̣c hi&ecirc;̣u quả.</p>\r\n</div>', '{\"Loại CPU\":\"AMD Ryzen 9 5900HX 3.3GHz up to 4.6GHz 16MB\",\"Loại card đồ họa\":\"NVIDIA GeForce RTX 3070 8GB GDDR6\",\"Dung lượng RAM\":\"16GB\",\"Loại RAM\":\"DDR4 3200Mhz, 2 khe cắm, hỗ trợ tối đa 32GB\",\"Ổ cứng\":\"1TB SSD PCIE G3X4 (2 slot)\",\"Kích thước màn hình\":\"15.6 inches\",\"Độ phân giải màn hình\":\"2560x1440 (Quad HD)\",\"Cổng giao tiếp\":\"1x Type C USB 3.2 Gen 2, 3x USB 3.2 Gen 1 Type-A, 1x HDMI 2.0b, 1x 3.5mm Audio Jack, 1x RJ45 LAN Jack, 1x Keystone\",\"Hệ điều hành\":\"Windows 10 Home\",\"Pin\":\"4 Cell 90WHrs\",\"Kích thước\":\"35.4 x 25.9 x 2.26 cm\",\"Trọng lượng\":\"2.3 kg\",\"Công nghệ màn hình\":\"Chất lượng màn hính NanoEdge, Độ bao phủ màu DCI-P3:100.00%\",\"Màn hình cảm ứng\":\"Không\",\"Chất liệu tấm nền\":\"Tấm nền IPS\",\"Độ phân giải\":\"2K (Quad HD)\",\"Wi-Fi\":\"Wi-Fi 6 802.11ax (2x2)\",\"Bluetooth\":\"v5.1\",\"Khe đọc thẻ nhớ\":\"Có\",\"Tính năng đặc biệt\":\"Ổ cứng SSD, Wi-Fi 6\"}', 1, 2, 2, 1, '2021-07-15 17:12:02', '2021-07-17 11:54:42', NULL);
INSERT INTO `products` VALUES (564, 'Laptop ASUS TUF Gaming FA506IV-HN202T', 'laptop-asus-tuf-gaming-fa506iv-hn202t', 120, 19000000, 28490000, 0, '<div class=\"blog-content\">\r\n<h2 style=\"text-align:left\"><strong>Asus TUF Gaming A15 FA506IV (HN202T) - Mạnh mẽ vượt trội, chiến mọi tựa game</strong></h2>\r\n\r\n<p style=\"text-align:left\">Khi n&oacute;i tới laptop gaming mọi người sẽ h&igrave;nh dung ra ngay những cỗ m&aacute;y si&ecirc;u nặng, si&ecirc;u to v&agrave; v&ocirc; c&ugrave;ng th&ocirc; cứng. Nhưng với&nbsp;laptop<strong> Asus TUF Gaming A15 FA506IV&nbsp;</strong>sẽ khiến bạn thay đổi ho&agrave;n to&agrave;n c&aacute;ch nh&igrave;n v&agrave;o d&ograve;ng laptop chơi game n&agrave;y. Được thiết kế gọn, cấu h&igrave;nh mạnh mẽ c&ugrave;ng những trang bị tối t&acirc;n nhất, đ&acirc;y l&agrave; một trong những chiếc laptop m&agrave; giới game thủ kh&ocirc;ng thể bỏ qua.</p>\r\n\r\n<h3 style=\"text-align:left\"><strong>Thiết kế nhỏ gọn, tinh tế, độ bền chuẩn qu&acirc;n đội</strong></h3>\r\n\r\n<p style=\"text-align:left\"><strong><a href=\"https://cellphones.com.vn/laptop/asus/gaming.html\" target=\"_blank\">Laptop Asus&nbsp;Gaming</a> TUF A15 FA506IV</strong>&nbsp;được thiết kế nhỏ hơn, gọn nhẹ hơn so với người tiền nhiệm. Khung m&aacute;y Fortress Grey mang đến sự lịch l&atilde;m v&agrave; b&oacute;ng bẩy.</p>\r\n\r\n<p style=\"text-align:left\"><img alt=\"Thiết kế nhỏ gọn, tinh tế, độ bền chuẩn quân đội\" class=\"cpslazy loaded\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/laptop/Laptop-asus-tuf-gaming-FA506IV-HN202T-1.jpg\" style=\"height:600px; width:100%\" /></p>\r\n\r\n<p style=\"text-align:left\">To&agrave;n th&acirc;n l&agrave; những đường n&eacute;t họa tiết tổ ong tinh tế ở khung m&aacute;y độc đ&aacute;o gi&uacute;p tản nhiệt tốt hơn. C&aacute;c đường kẻ sọc đối lập dọc th&acirc;n m&aacute;y thanh tho&aacute;t. Đặc biệt l&agrave; thiết kế Fortress Gray cực sang trọng tr&ecirc;n mặt lưng kết hợp với logo TUF gaming đẳng cấp. Tất cả tạo n&ecirc;n diện mạo đầy c&aacute; t&iacute;nh v&agrave; đẳng cấp cho một chiếc laptop chuẩn game thủ.</p>\r\n\r\n<p style=\"text-align:left\">Asus TUF Gaming FA506IV&nbsp;đ&atilde; vượt qua h&agrave;ng loạt b&agrave;i test trong c&aacute;c điều kiện kh&aacute;c nhau v&agrave; được chứng nhận c&oacute; thể hoạt động tốt trong việc sử dụng hằng ng&agrave;y. Độ bền đ&atilde; được đẩy tới giới hạn mang đến sự đảm bảo tốt nhất cho c&aacute;c game thủ n&oacute;ng t&iacute;nh hay an to&agrave;n trong c&aacute;c t&igrave;nh huống va chạm.</p>\r\n\r\n<p style=\"text-align:left\"><em>&gt;&gt;&gt; T&igrave;m hiểu chi tiết chiếc&nbsp;<a href=\"https://cellphones.com.vn/laptop-asus-fa506ii-al012t-amd-ryzen-5-4600h-ram-8gb-ssd-512gb-15-6inch-144hz-vga-4gb.html\" target=\"_blank\">Laptop ASUS TUF Gaming FA506II-AL012T</a> mang hiệu năng hoạt động v&ocirc; c&ugrave;ng ấn tượng</em></p>\r\n\r\n<h3 style=\"text-align:left\"><strong>M&agrave;n h&igrave;nh 15,6 inch hỗ trợ c&ocirc;ng nghệ Adaptive sync</strong></h3>\r\n\r\n<p style=\"text-align:left\">M&agrave;n h&igrave;nh của laptop TUF Gaming FA506IV được chế tạo với khung viền mỏng tối đa chỉ 6.5mm. K&iacute;ch thước 15.6 inch c&ugrave;ng độ ph&acirc;n giải Full HD mang đến h&igrave;nh ảnh v&ocirc; c&ugrave;ng tuyệt đẹp.</p>\r\n\r\n<p style=\"text-align:left\">&nbsp;</p>\r\n\r\n<p style=\"text-align:left\">Đặc biệt, m&agrave;n h&igrave;nh 144Hz, được hỗ trợ c&ocirc;ng nghệ Adaptive sync gi&uacute;p khung h&igrave;nh trong game được t&aacute;i hiện một c&aacute;ch mượt m&agrave;. Tốc độ khung h&igrave;nh trở n&ecirc;n nhanh v&agrave; giảm thiểu độ trễ cũng như hiện tượng x&eacute; h&igrave;nh.</p>\r\n\r\n<h3 style=\"text-align:left\"><strong>Bộ vi xử l&yacute; Ryzen 7 4800H, card đồ họa Nvidia RTX2060 TI 6GB</strong></h3>\r\n\r\n<p style=\"text-align:left\"><strong>Asus TUF Gaming FA506IV</strong>&nbsp;được trang bị tr&ecirc;n m&igrave;nh sức mạnh vượt trội của bộ vi xử l&yacute; Ryzen 7 4800H d&agrave;nh ri&ecirc;ng cho c&aacute;c game thủ. Với 8 l&otilde;i 16 luồng, sản xuất tr&ecirc;n tiến tr&igrave;nh 7nm c&ugrave;ng với đ&oacute; l&agrave; tốc độ 2.9 GHz - 4.0 GHz kh&ocirc;ng chỉ gi&uacute;p m&aacute;y chạy cực nhanh m&agrave; c&ograve;n c&oacute; thể k&iacute;ch hoạt đa luồng gi&uacute;p m&aacute;y xử l&yacute; mượt m&agrave; c&aacute;c t&aacute;c vụ đa nhiệm nặng.</p>\r\n\r\n<p style=\"text-align:left\">&nbsp;</p>\r\n\r\n<p style=\"text-align:left\">Ngo&agrave;i ra, ổ cứng SSD 1TB NVMe Pcie tr&ecirc;n m&aacute;y c&ugrave;ng RAM 16GB DDR4 c&oacute; khả năng n&acirc;ng cấp dễ d&agrave;ng mang đến cho bạn một hiệu năng như những g&igrave; mong đợi.</p>\r\n\r\n<p style=\"text-align:left\">Đặc biệt m&aacute;y c&ograve;n sở hữu card đồ họa với kiến tr&uacute;c Turing danh tiếng Nvidia RTX2060 TI 6GB gi&uacute;p bạn chơi tốt mọi game eSports v&agrave; sẵn s&agrave;ng th&aacute;ch đấu trong c&aacute;c tựa game bom tấn AAA. C&ocirc;ng nghệ d&ograve; tia theo thời gian thực, tr&iacute; tuệ nh&acirc;n tạo AI trong game sẽ mang đến cho bạn những trải nghiệm cực đỉnh.</p>\r\n\r\n<h3 style=\"text-align:left\"><strong>B&agrave;n ph&iacute;m Hyperstrike, c&ocirc;ng nghệ độc quyền Hypercool</strong></h3>\r\n\r\n<p style=\"text-align:left\">Asus TUF Gaming A15 FA506IV<strong>&nbsp;</strong>sở hữu b&agrave;n ph&iacute;m Hyperstrike với h&agrave;nh tr&igrave;nh ph&iacute;m 1.8 mm 20 triệu lần nhấn cho nhiều năm l&agrave;m việc v&agrave; chơi game.&nbsp;</p>\r\n\r\n<p style=\"text-align:left\">T&iacute;nh năng RGB-backlighting tr&ecirc;n b&agrave;n ph&iacute;m được điều khiển th&ocirc;ng qua TUF Aura Core gi&uacute;p thể hiện phong c&aacute;ch độc đ&aacute;o của game thủ.</p>\r\n\r\n<p style=\"text-align:left\">&nbsp;</p>\r\n\r\n<p style=\"text-align:left\">M&aacute;y c&ograve;n được trang bị c&ocirc;ng nghệ HyperCool độc quyền bao gồm hệ thống Anti-Dust Cooling với 3 chế độ quạt kh&aacute;c nhau. Đặc biệt c&ocirc;ng nghệ Fan Overboost gi&uacute;p nhiệt độ lu&ocirc;n ở mức ổn định trong khi chơi game nặng.&nbsp;</p>\r\n\r\n<p style=\"text-align:left\">B&ecirc;n cạnh đ&oacute;, 2 quạt tản nhiệt độc lập được cải thiện bởi nắp h&igrave;nh thang mang đến khả năng tản nhiệt hơn l&ecirc;n đến 25% so với quạt th&ocirc;ng thường.</p>\r\n\r\n<h3 style=\"text-align:left\"><strong>&Acirc;m thanh ch&acirc;n thực, phần mềm hỗ trợ độc quyền</strong></h3>\r\n\r\n<p style=\"text-align:left\">Asus TUF Gaming FA506IV<strong>&nbsp;</strong>được t&iacute;ch hợp c&ocirc;ng nghệ DTS Headphone: X&trade; mang đến chất lượng &acirc;m thanh ch&acirc;n thật như nhập vai v&agrave;o tr&ograve; chơi.&nbsp;</p>\r\n\r\n<p style=\"text-align:left\">B&ecirc;n cạnh đ&oacute;, bạn c&oacute; thể c&agrave;i đặt tối ưu cho tr&ograve; chơi, &acirc;m nhạc, xem phim ảnh hay thể thao. T&ugrave;y chỉnh Equalizer để c&oacute; trải nghiệm &acirc;m thanh tốt nhất.</p>\r\n\r\n<p style=\"text-align:left\">&nbsp;</p>\r\n\r\n<p style=\"text-align:left\">Armory Crate đặt hệ thống kiểm tra, th&ocirc;ng tin v&agrave; điều khiển LED trong một app duy nhất. Bạn c&oacute; thể thay đổi c&agrave;i đặt cũng như thiết lập một c&aacute;ch dễ d&agrave;ng v&agrave; sẽ tự động điều chỉnh khi khởi chạy c&aacute;c tựa game y&ecirc;u th&iacute;ch.</p>\r\n\r\n<h3 style=\"text-align:left\"><strong>Wifi v&agrave; cổng kết nối</strong></h3>\r\n\r\n<p style=\"text-align:left\">Asus TUF Gaming FA506IV&nbsp;sở hữu chuẩn Wi-Fi nhanh nhất hiện nay Intel 802.11ac Wave 2 với tốc độ truyền dữ liệu l&ecirc;n đến 1.7Gbps. Cấu h&igrave;nh MIMO 2&times;2 mang đến phạm vi phủ s&oacute;ng rộng hơn 30%.</p>\r\n\r\n<p style=\"text-align:left\">&nbsp;</p>\r\n\r\n<p style=\"text-align:left\">M&aacute;y trang bị một cổng USB 2.0 v&agrave; hai cổng USB 3.1 Gen 1 loại A. Cổng HDMI 2.0 để kết nối với m&agrave;n h&igrave;nh ngo&agrave;i. Tất cả được thiết kế nằm ở ph&iacute;a b&ecirc;n tr&aacute;i của m&aacute;y. C&ograve;n phần b&ecirc;n phải sẽ để trống mang đến sự thuận tiện hơn trong việc sử dụng chuột.</p>\r\n</div>', '{\"Loại CPU\":\"AMD Ryzen 9 5900HX 3.3GHz up to 4.6GHz 16MB\",\"Loại card đồ họa\":\"NVIDIA GeForce RTX 3070 8GB GDDR6\",\"Dung lượng RAM\":\"16GB\",\"Loại RAM\":\"DDR4 3200Mhz, 2 khe cắm, hỗ trợ tối đa 32GB\",\"Ổ cứng\":\"1TB SSD PCIE G3X4 (2 slot)\",\"Kích thước màn hình\":\"15.6 inches\",\"Độ phân giải màn hình\":\"2560x1440 (Quad HD)\",\"Cổng giao tiếp\":\"1x Type C USB 3.2 Gen 2, 3x USB 3.2 Gen 1 Type-A, 1x HDMI 2.0b, 1x 3.5mm Audio Jack, 1x RJ45 LAN Jack, 1x Keystone\",\"Hệ điều hành\":\"Windows 10 Home\",\"Pin\":\"4 Cell 90WHrs\",\"Kích thước\":\"35.4 x 25.9 x 2.26 cm\",\"Trọng lượng\":\"2.3 kg\",\"Công nghệ màn hình\":\"Chất lượng màn hính NanoEdge, Độ bao phủ màu DCI-P3:100.00%\",\"Màn hình cảm ứng\":\"Không\",\"Chất liệu tấm nền\":\"Tấm nền IPS\",\"Độ phân giải\":\"2K (Quad HD)\",\"Wi-Fi\":\"Wi-Fi 6 802.11ax (2x2)\",\"Bluetooth\":\"v5.1\",\"Khe đọc thẻ nhớ\":\"Có\",\"Tính năng đặc biệt\":\"Ổ cứng SSD, Wi-Fi 6\"}', 1, 2, 2, 1, '2021-07-15 17:14:47', '2021-07-17 11:54:42', NULL);
INSERT INTO `products` VALUES (565, 'Laptop ASUS Zenbook UX425EA', 'laptop-asus-zenbook-ux425ea', 60, 25000000, 30690000, 0, '<div class=\"blog-content\">\r\n<h2 style=\"text-align:justify\"><strong>Laptop Asus Zenbook UX425EA-KI439T nhỏ gọn nhưng v&ocirc; c&ugrave;ng mạnh mẽ</strong></h2>\r\n\r\n<p style=\"text-align:justify\">Thuộc d&ograve;ng Zenbook cao cấp <strong><a href=\"https://cellphones.com.vn/laptop/asus/zenbook.html\" title=\"Laptop Asus Zenbook\">laptop Asus Zenbook</a> UX425EA-KI439T</strong> được trang bị ng&ocirc;n ngữ thiết kế mới c&ugrave;ng h&agrave;ng loạt những trang bị hiện đại. Đ&acirc;y chắc chắn sẽ l&agrave; thiết kế mang đến cho người d&ugrave;ng một hiệu năng mạnh mẽ c&ugrave;ng sự linh hoạt v&agrave; tiện lợi trong qu&aacute; tr&igrave;nh di chuyển.</p>\r\n\r\n<h3 style=\"text-align:justify\"><strong>Thiết kế sang trọng mới mẻ, m&agrave;n h&igrave;nh sống động</strong></h3>\r\n\r\n<p style=\"text-align:justify\">Asus Zenbook UX425EA-KI439T được kho&aacute;c tr&ecirc;n m&igrave;nh thiết kế mới mẻ. Vỏ ngo&agrave;i được ho&agrave;n thiện bằng chất liệu nh&ocirc;m cao cấp. B&ecirc;n cạnh đ&oacute; l&agrave; trọng lượng nhẹ c&ugrave;ng độ mỏng tinh tế gi&uacute;p người d&ugrave;ng c&oacute; thể dễ d&agrave;ng mang theo trong mọi h&agrave;nh tr&igrave;nh.</p>\r\n\r\n<p style=\"text-align:justify\"><img alt=\"Laptop Asus Zenbook UX425EA-KI439T\" class=\"cpslazy loaded\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/laptop/Laptop-Asus-zenbook-UX425EA-KI439T-1_1_.jpg\" style=\"height:600px; width:100%\" /></p>\r\n\r\n<p style=\"text-align:justify\">M&aacute;y c&ograve;n được trang bị m&agrave;n h&igrave;nh lớn với độ ph&acirc;n giải Full-HD, viền m&agrave;n h&igrave;nh NanoEdge si&ecirc;u mỏng mang đến tỷ lệ m&agrave;n h&igrave;nh tr&ecirc;n th&acirc;n m&aacute;y l&ecirc;n tới 90% v&agrave; khả năng hiển thị rộng hơn. M&aacute;y c&ograve;n c&oacute; độ s&aacute;ng m&agrave;n h&igrave;nh cực lớn để người d&ugrave;ng c&oacute; trải nghiệm h&igrave;nh ảnh tốt hơn trong điều kiện &aacute;nh s&aacute;ng mạnh.</p>\r\n\r\n<h3 style=\"text-align:justify\"><strong>Cấu h&igrave;nh mạnh mẽ, độ bền chuẩn qu&acirc;n đội</strong></h3>\r\n\r\n<p style=\"text-align:justify\">Mang đến hiệu năng mạnh mẽ cho laptop Asus Zenbook UX425EA-KI439T l&agrave; bộ vi xử l&yacute; Intel thế hệ mới nhất. C&ugrave;ng với đ&oacute; l&agrave; RAM chuẩn, c&ocirc;ng nghệ wifi hiện đại gi&uacute;p mọi t&aacute;c vụ được thực hiện một c&aacute;ch mượt m&agrave; cũng như mang lại kết nối vượt trội với thời gian.</p>\r\n\r\n<p style=\"text-align:justify\"><img alt=\"Laptop Asus Zenbook UX425EA-KI439T\" class=\"cpslazy loaded\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/laptop/Laptop-Asus-zenbook-UX425EA-KI439T-2_1_.jpg\" style=\"height:600px; width:100%\" /></p>\r\n\r\n<p style=\"text-align:justify\">Với Zenbook UX425EA-KI439T người d&ugrave;ng c&ograve;n ho&agrave;n to&agrave;n y&ecirc;n t&acirc;m về độ bền của m&aacute;y khi ch&uacute;ng đ&atilde; vượt qua c&aacute;c b&agrave;i test khắc nghiệt chuẩn qu&acirc;n đội. B&ecirc;n cạnh đ&oacute;, khả năng lưu trữ lớn, dung lượng pin khủng c&ugrave;ng cổng kết nối đa dạng sẽ gi&uacute;p người d&ugrave;ng c&oacute; những trải nghiệm ho&agrave;n hảo nhất.</p>\r\n</div>', '{\"Loại CPU\":\"AMD Ryzen 9 5900HX 3.3GHz up to 4.6GHz 16MB\",\"Loại card đồ họa\":\"NVIDIA GeForce RTX 3070 8GB GDDR6\",\"Dung lượng RAM\":\"16GB\",\"Loại RAM\":\"DDR4 3200Mhz, 2 khe cắm, hỗ trợ tối đa 32GB\",\"Ổ cứng\":\"1TB SSD PCIE G3X4 (2 slot)\",\"Kích thước màn hình\":\"15.6 inches\",\"Độ phân giải màn hình\":\"2560x1440 (Quad HD)\",\"Cổng giao tiếp\":\"1x Type C USB 3.2 Gen 2, 3x USB 3.2 Gen 1 Type-A, 1x HDMI 2.0b, 1x 3.5mm Audio Jack, 1x RJ45 LAN Jack, 1x Keystone\",\"Hệ điều hành\":\"Windows 10 Home\",\"Pin\":\"4 Cell 90WHrs\",\"Kích thước\":\"35.4 x 25.9 x 2.26 cm\",\"Trọng lượng\":\"2.3 kg\",\"Công nghệ màn hình\":\"Chất lượng màn hính NanoEdge, Độ bao phủ màu DCI-P3:100.00%\",\"Màn hình cảm ứng\":\"Không\",\"Chất liệu tấm nền\":\"Tấm nền IPS\",\"Độ phân giải\":\"2K (Quad HD)\",\"Wi-Fi\":\"Wi-Fi 6 802.11ax (2x2)\",\"Bluetooth\":\"v5.1\",\"Khe đọc thẻ nhớ\":\"Có\",\"Tính năng đặc biệt\":\"Ổ cứng SSD, Wi-Fi 6\"}', 1, 2, 2, 1, '2021-07-15 17:17:59', '2021-07-17 11:54:42', NULL);
INSERT INTO `products` VALUES (566, 'Laptop ASUS VivoBook 15 A515EA', 'laptop-asus-vivobook-15-a515ea', 60, 11000000, 14790000, 0, '<p><strong><a href=\"https://cellphones.com.vn/laptop/asus/vivobook.html\" target=\"_blank\">Laptop ASUS VivoBook</a>&nbsp;15 A515EA</strong>&nbsp;chiếc m&aacute;y ch&iacute;nh h&atilde;ng, chất lượng, c&oacute; khả năng đ&aacute;p ứng mọi t&aacute;c vụ văn ph&ograve;ng một c&aacute;ch hiệu quả. Ngo&agrave;i ra, m&aacute;y cũng lu&ocirc;n được trang bị một ổ nhớ SSD với khả năng tăng tốc độ xử l&yacute; nhanh nhằm mang đến những trải nghiệm th&uacute; vị cho người d&ugrave;ng.<br />\r\nĐồng thời, cũng đừng qu&ecirc;n kh&aacute;m ph&aacute; th&ecirc;m mẫu&nbsp;<a href=\"https://cellphones.com.vn/laptop-asus-vivobook-x515ea-i3-ram-4gb.html\" target=\"_blank\">laptop ASUS Vivobook 15 X515EA-EJ062T</a>, một trong những d&ograve;ng laptop văn ph&ograve;ng c&oacute; khả năng giải quyết nhiều t&aacute;c vụ nặng một c&aacute;ch ổn định.</p>', '{\"Loại CPU\":\"AMD Ryzen 9 5900HX 3.3GHz up to 4.6GHz 16MB\",\"Loại card đồ họa\":\"NVIDIA GeForce RTX 3070 8GB GDDR6\",\"Dung lượng RAM\":\"16GB\",\"Loại RAM\":\"DDR4 3200Mhz, 2 khe cắm, hỗ trợ tối đa 32GB\",\"Ổ cứng\":\"1TB SSD PCIE G3X4 (2 slot)\",\"Kích thước màn hình\":\"15.6 inches\",\"Độ phân giải màn hình\":\"2560x1440 (Quad HD)\",\"Cổng giao tiếp\":\"1x Type C USB 3.2 Gen 2, 3x USB 3.2 Gen 1 Type-A, 1x HDMI 2.0b, 1x 3.5mm Audio Jack, 1x RJ45 LAN Jack, 1x Keystone\",\"Hệ điều hành\":\"Windows 10 Home\",\"Pin\":\"4 Cell 90WHrs\",\"Kích thước\":\"35.4 x 25.9 x 2.26 cm\",\"Trọng lượng\":\"2.3 kg\",\"Công nghệ màn hình\":\"Chất lượng màn hính NanoEdge, Độ bao phủ màu DCI-P3:100.00%\",\"Màn hình cảm ứng\":\"Không\",\"Chất liệu tấm nền\":\"Tấm nền IPS\",\"Độ phân giải\":\"2K (Quad HD)\",\"Wi-Fi\":\"Wi-Fi 6 802.11ax (2x2)\",\"Bluetooth\":\"v5.1\",\"Khe đọc thẻ nhớ\":\"Có\",\"Tính năng đặc biệt\":\"Ổ cứng SSD, Wi-Fi 6\"}', 1, 2, 2, 1, '2021-07-15 17:21:15', '2021-07-17 11:54:42', NULL);
INSERT INTO `products` VALUES (567, 'Laptop Huawei Matebook 13 2020', 'laptop-huawei-matebook-13-2020', 60, 16000000, 21990000, 0, '<div class=\"blog-content\">\r\n<h2><strong>Huawei Matebook 13 2020:</strong><strong>&nbsp;Đỉnh cao kết nối &ndash; Đỉnh cao hiệu năng</strong></h2>\r\n\r\n<p>Bạn đang t&igrave;m kiếm cho bản th&acirc;n m&igrave;nh một chiếc laptop với c&aacute;c cấu h&igrave;nh phần cứng đ&aacute;p ứng cả cho việc văn ph&ograve;ng v&agrave; chơi game giải tr&iacute;? Bạn đang một trong những sản phẩm điện thoại Huawei v&agrave; mong muốn kiếm một chiếc laptop của Huawei với nhiều c&ocirc;ng nghệ thu h&uacute;t nhất? Mời bạn tham khảo về b&agrave;i viết&nbsp;<strong><a href=\"https://cellphones.com.vn/laptop/huawei/matebook.html\" target=\"_blank\">Huawei Matebook</a> 13</strong> b&ecirc;n dưới.</p>\r\n\r\n<h3><strong>Thiết kế nguy&ecirc;n khối sang trọng c&ugrave;ng trọng lượng chỉ xấp xỉ 1.3kg</strong></h3>\r\n\r\n<p>Mang trong m&igrave;nh một thiết kế nguy&ecirc;n khối s&aacute;ng b&oacute;ng với c&aacute;c thiết kế diamond cut hiện đại v&agrave; b&oacute;ng bẩy, mang lại cho chiếc laptop Huawei Matebook 13 2020 một vẻ đẹp ho&agrave;n mỹ.&nbsp;&nbsp;</p>\r\n\r\n<p><img alt=\"Thiết kế nguyên khối bằng kim loại sang trọng cùng trọng lượng chỉ xấp xỉ 1.3kg\" class=\"cpslazy loaded\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/laptop/laptop-huawei-matebook-13-2020-1_1.jpg\" style=\"height:600px; width:100%\" /></p>\r\n\r\n<p>Việc chiếc laptop Huawei Matebook n&agrave;y chỉ c&oacute; độ mỏng l&agrave; 14.9 mm, tức nghĩa l&agrave; c&aacute;c trang bị b&ecirc;n trong đều được tối ưu kh&ocirc;ng gian v&agrave; cao cấp nhất c&oacute; thể th&igrave; ch&uacute;ng chỉ mang trọng lượng l&agrave; chưa đến 1.3kg. Việc n&agrave;y gi&uacute;p bạn rất nhiều trong việc sử dụng trong thời gian d&agrave;i v&agrave; mang v&aacute;c ch&uacute;ng đến nhiều nơi kh&aacute;c nhau m&agrave; kh&ocirc;ng cảm thấy nặng nề.</p>\r\n\r\n<h3><strong>M&agrave;n h&igrave;nh IPS 13 inches chất lượng 2K c&ugrave;ng độ s&aacute;ng cao với 300 nits</strong></h3>\r\n\r\n<p>M&agrave;n h&igrave;nh của Huawei Matebook 13 l&agrave; một điểm s&aacute;ng với c&ocirc;ng nghệ m&agrave;n h&igrave;nh IPS cho m&agrave;u sắc c&oacute; độ ch&iacute;nh x&aacute;c cao. C&ugrave;ng với đ&oacute;, m&agrave;n h&igrave;nh cũng c&oacute; độ rộng 13 inches v&agrave; chất lượng hiển thị m&agrave;u sắc 2K. Điều n&agrave;y gi&uacute;p bạn c&oacute; thể sử dụng m&aacute;y t&iacute;nh trong c&ocirc;ng việc chỉnh sửa ảnh hay video tốt nhất.</p>\r\n\r\n<p><img alt=\"Màn hình IPS 13 inches chất lượng 2K cùng độ sáng cao với 300 nits\" class=\"cpslazy loaded\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/laptop/laptop-huawei-matebook-13-2020-2.jpg\" style=\"height:600px; width:100%\" /></p>\r\n\r\n<p>Nhằm đảm bảo người d&ugrave;ng c&oacute; thể sử dụng Matebook 13 của m&igrave;nh trong một kh&ocirc;ng gian c&oacute; &aacute;nh s&aacute;ng lớn th&igrave; Huawei đ&atilde; trang bị một m&agrave;n h&igrave;nh c&oacute; độ s&aacute;ng l&ecirc;n đến 300 nits. Ch&uacute;ng c&oacute; thể gi&uacute;p bạn rất nhiều trong trường hợp sử dụng dưới &aacute;nh nắng chiếu thẳng hay l&agrave; nguồn s&aacute;ng qu&aacute; lớn th&igrave; kh&ocirc;ng ngại bị ch&oacute;i.</p>\r\n\r\n<h3><strong>Con chip Intel i5-10210U mạnh mẽ&nbsp;&nbsp;</strong></h3>\r\n\r\n<p>Con chip xử l&yacute; tr&ecirc;n Huawei Matebook 13 2020 cũng l&agrave; loại chip mới nhất c&oacute; thể tiết kiệm điện năng nhất c&oacute; thể, chip Intel i5-10210U mang lại cho người d&ugrave;ng một hiệu năng nhanh ch&oacute;ng, mượt m&agrave; với 4 nh&acirc;n v&agrave; 8 luồng. Gi&uacute;p cho mọi thao t&aacute;c v&agrave; điều chỉnh lu&ocirc;n được xử l&yacute; nhanh ch&oacute;ng nhất.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3><strong>Thanh ram lớn l&ecirc;n đến 16GB đa nhiệm tốt c&ugrave;ng khả năng lưu trữ nhanh ch&oacute;ng với 512GB SSD</strong></h3>\r\n\r\n<p>Một chiếc laptop để c&oacute; thể nhanh ch&oacute;ng mạnh mẽ th&igrave; phải c&oacute; ram lớn, chip mạnh v&agrave; ổ cứng SSD để khởi động nhanh ch&oacute;ng. Thanh ram 16GB tr&ecirc;n <strong>Matebook 13</strong> c&oacute; t&aacute;c dụng gi&uacute;p chiếc m&aacute;y ổn định trong mọi thao t&aacute;c v&agrave; kể cả việc sử dụng nhiều ứng dụng c&ugrave;ng một l&uacute;c c&oacute; thể được tối ưu ho&aacute; hơn.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>B&ecirc;n cạnh c&aacute;c phần cứng tr&ecirc;n th&igrave; SSD 512GB cũng mang lại cho Matebook 13 một trải nghiệm mở ứng dụng v&agrave; nguồn một c&aacute;ch nhanh ch&oacute;ng nhất c&oacute; thể. Nhờ v&agrave;o ổ SSD n&agrave;y m&agrave; laptop đ&atilde; mạnh nay c&ograve;n mạnh hơn v&agrave; nhanh hơn cho c&aacute;c thao t&aacute;c sử dụng.</p>\r\n\r\n<h3><strong>Cổng kết nối đa dạng c&ugrave;ng khả năng sử dụng cả ng&agrave;y với vi&ecirc;n pin liền 41.7 Whr</strong></h3>\r\n\r\n<p>Mặc d&ugrave; c&oacute; một thiết kế kh&aacute; mỏng v&agrave; mang danh ultrabook nhưng c&aacute;c cổng kết nối của laptop <strong>Huawei Matebook 13</strong> cũng kh&ocirc;ng hề bị lượt bỏ đi phần n&agrave;o. Ch&uacute;ng được trang bị đầy đủ từ cổng tai nghe 3.5mm cho đến cổng USB-C. Việc trang bị cổng đầy đủ gi&uacute;p cho người d&ugrave;ng c&oacute; đươc một trải nghiệm thuận lợi nhất c&oacute; thể.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Vi&ecirc;n pin liền c&oacute; dung lượng 41.7 Whr gi&uacute;p cho Huawei Matebook 13 c&oacute; khả năng sử dụng l&ecirc;n đến 11 giờ. Việc n&agrave;y gi&uacute;p bạn c&oacute; thể duy tr&igrave; sử dụng trong thời gian d&agrave;i để giải quyết c&ocirc;ng việc một c&aacute;ch nhanh ch&oacute;ng nhất c&oacute; thể.</p>\r\n</div>', '{\"Loại CPU\":\"AMD Ryzen 9 5900HX 3.3GHz up to 4.6GHz 16MB\",\"Loại card đồ họa\":\"NVIDIA GeForce RTX 3070 8GB GDDR6\",\"Dung lượng RAM\":\"16GB\",\"Loại RAM\":\"DDR4 3200Mhz, 2 khe cắm, hỗ trợ tối đa 32GB\",\"Ổ cứng\":\"1TB SSD PCIE G3X4 (2 slot)\",\"Kích thước màn hình\":\"15.6 inches\",\"Độ phân giải màn hình\":\"2560x1440 (Quad HD)\",\"Cổng giao tiếp\":\"1x Type C USB 3.2 Gen 2, 3x USB 3.2 Gen 1 Type-A, 1x HDMI 2.0b, 1x 3.5mm Audio Jack, 1x RJ45 LAN Jack, 1x Keystone\",\"Hệ điều hành\":\"Windows 10 Home\",\"Pin\":\"4 Cell 90WHrs\",\"Kích thước\":\"35.4 x 25.9 x 2.26 cm\",\"Trọng lượng\":\"2.3 kg\",\"Công nghệ màn hình\":\"Chất lượng màn hính NanoEdge, Độ bao phủ màu DCI-P3:100.00%\",\"Màn hình cảm ứng\":\"Không\",\"Chất liệu tấm nền\":\"Tấm nền IPS\",\"Độ phân giải\":\"2K (Quad HD)\",\"Wi-Fi\":\"Wi-Fi 6 802.11ax (2x2)\",\"Bluetooth\":\"v5.1\",\"Khe đọc thẻ nhớ\":\"Có\",\"Tính năng đặc biệt\":\"Ổ cứng SSD, Wi-Fi 6\"}', 1, 2, 4, 1, '2021-07-15 17:25:24', '2021-07-17 11:54:42', NULL);
INSERT INTO `products` VALUES (568, 'Laptop Huawei Matebook D 15', 'laptop-huawei-matebook-d-15', 60, 14000000, 18900000, 0, '<div class=\"blog-content\">\r\n<h2>Laptop Huawei Matebook D15&nbsp;thiết kế sang trọng v&agrave; cấu h&igrave;nh mạnh mẽ</h2>\r\n\r\n<p>Khi nhu cầu sử dụng laptop ng&agrave;y c&agrave;ng tăng th&igrave; đương nhi&ecirc;n người d&ugrave;ng sẽ c&oacute; những y&ecirc;u cầu cao hơn đối với một chiếc laptop để đảm bảo đủ mạnh mẽ xử l&iacute; tốt những dữ liệu phục vụ cho nhu cầu sử dụng của bạn. Một chiếc <a href=\"https://cellphones.com.vn/laptop/huawei/matebook.html\" target=\"_blank\" title=\"Laptop huawei matebook\">laptop Huawei Matebook</a> c&oacute; ưu điểm to&agrave;n diện từ thiết kế, m&agrave;n h&igrave;nh cho đến cấu h&igrave;nh, dung lượng pin, lẫn bảo mật l&agrave; điều m&agrave; đại đa số người d&ugrave;ng hiện nay t&igrave;m kiếm v&agrave; đ&ograve;i hỏi. C&oacute; vẻ những y&ecirc;u cầu nghe kh&aacute; kh&oacute; c&oacute; thể đ&aacute;p ứng, nhưng&nbsp;laptop <strong>Huawei Matebook D15</strong>&nbsp;ch&iacute;nh l&agrave; sản phẩm hội tụ đủ những ưu điểm đ&oacute;.</p>\r\n\r\n<h3>Trọng lượng nhẹ, với chất liệu hợp kim nh&ocirc;m&nbsp;</h3>\r\n\r\n<p>Laptop Huawei Matebook D15 l&agrave; sản phẩm của tập đo&agrave;n c&ocirc;ng nghệ Huawei nổi tiếng, chiếc laptop n&agrave;y c&oacute; trọng lượng rất nhẹ, chỉ khoảng 1.53kg. Được l&agrave;m từ chất liệu hợp kim nh&ocirc;m cao cấp n&ecirc;n Matebook D15 c&oacute; độ bền tốt v&agrave; vẻ ngo&agrave;i rất bắt mắt.</p>\r\n\r\n<p><em>&gt;&gt;&gt; <strong>Xem th&ecirc;m</strong>:&nbsp;<a href=\"https://cellphones.com.vn/laptop-huawei-matebook-13.html\" target=\"_blank\">Laptop Huawei Matebook 13 2020</a> thiết kế sang trọng, hiệu năng đỉnh cao</em></p>\r\n\r\n<p><img alt=\"Trọng lượng nhẹ, với chất liệu hợp kim nhôm \" class=\"cpslazy loaded\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/laptop/laptop-huawei-matebook-d15-1_1.jpg\" style=\"height:600px; width:100%\" /></p>\r\n\r\n<p>Sở hữu m&agrave;u x&aacute;m bạc sang trọng khiến Huawei Matebook D15 trở n&ecirc;n nổi bật v&agrave; rất bắt mắt. Chiếc laptop n&agrave;y gi&uacute;p người d&ugrave;ng trở n&ecirc;n v&ocirc; c&ugrave;ng thanh lịch v&agrave; chuy&ecirc;n nghiệp, th&iacute;ch hợp để sử dụng trong hầu hết mọi ho&agrave;n cảnh.</p>\r\n\r\n<h3>M&agrave;n h&igrave;nh 15.6&nbsp;inch&nbsp;Full HD,&nbsp;vi xử l&iacute; AMD Ryzen 5 3500U</h3>\r\n\r\n<p>M&agrave;n h&igrave;nh của Huawei Matebook D15 rộng 15.6 inch, với độ lớn n&agrave;y một thế giới rộng lớn sẽ như được hiển thị trước mắt bạn, từng chi tiết đều được hiện l&ecirc;n r&otilde; r&agrave;ng. C&ugrave;ng với độ ph&acirc;n giải Full HD n&ecirc;n h&igrave;nh ảnh bạn thấy được đều sống động v&agrave; c&oacute; độ ch&acirc;n thực cao. Kh&ocirc;ng chỉ thế, m&agrave;n h&igrave;nh của Huawei Matebook D15 c&ograve;n hạn chế &aacute;nh s&aacute;ng độc hại, an to&agrave;n v&agrave; thoải m&aacute;i cho đ&ocirc;i mắt của bạn d&ugrave; phải nh&igrave;n v&agrave;o m&agrave;n h&igrave;nh thời gian d&agrave;i.</p>\r\n\r\n<p>Laptop Huawei Matebook D15 được trang bị vi xử l&iacute; AMD Ryzen 5 3500U kh&ocirc;ng chỉ gi&uacute;p tăng hiệu suất m&agrave; c&ograve;n n&acirc;ng cao tốc độ xử l&iacute; t&aacute;c vụ m&agrave; bạn y&ecirc;u cầu. Với chip n&agrave;y, Huawei Matebook D15 c&oacute; thể xử l&iacute; đa nhiệm dễ d&agrave;ng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ngo&agrave;i ra, chiếc laptop n&agrave;y sở hữu RAM 8GB v&agrave; SSD l&ecirc;n đến 512GB, mức Ram n&agrave;y sẽ hỗ trợ rất tốt cho bộ vi xử l&iacute; để c&oacute; hiệu suất tối ưu. SSD 512GB sẽ khiến bạn kh&ocirc;ng phải lo lắng về việc kh&ocirc;ng đủ dung lượng bộ nhớ, đ&aacute;p ứng tốt cho cả những người d&ugrave;ng l&agrave;m việc hay học tập.</p>\r\n\r\n<h3>Pin 42Wh sạc 53% trong 30 ph&uacute;t, c&ugrave;ng bluetooth 5.0 v&agrave; bảo mật v&acirc;n tay</h3>\r\n\r\n<p>Một ưu điểm kh&aacute;c kh&aacute; nổi trội của laptop Huawei Matebook D15 ch&iacute;nh l&agrave; pin 42Wh với khả năng sạc nhanh 53% chỉ trong 30 ph&uacute;t. Ch&iacute;nh v&igrave; thế, chiếc laptop n&agrave;y kh&ocirc;ng chỉ cho thời gian sử dụng l&acirc;u, m&agrave; c&ograve;n c&oacute; thời gian sạc rất nhanh, tiết kiệm nhiều thời gian.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ngo&agrave;i ra, Huawei Matebook D15 c&ograve;n được trang bị bluetooth 5.0 mới nhất hiện nay với t&iacute;n hiệu ổn định v&agrave; phủ s&oacute;ng trong phạm vi 10m. Ngo&agrave;i ra, Huawei Matebook D15 c&ograve;n c&oacute; t&iacute;nh năng khởi động laptop bằng v&acirc;n tay, n&acirc;ng cao t&iacute;nh bảo mật cho laptop.</p>\r\n</div>', '{\"Loại CPU\":\"AMD Ryzen 9 5900HX 3.3GHz up to 4.6GHz 16MB\",\"Loại card đồ họa\":\"NVIDIA GeForce RTX 3070 8GB GDDR6\",\"Dung lượng RAM\":\"16GB\",\"Loại RAM\":\"DDR4 3200Mhz, 2 khe cắm, hỗ trợ tối đa 32GB\",\"Ổ cứng\":\"1TB SSD PCIE G3X4 (2 slot)\",\"Kích thước màn hình\":\"15.6 inches\",\"Độ phân giải màn hình\":\"2560x1440 (Quad HD)\",\"Cổng giao tiếp\":\"1x Type C USB 3.2 Gen 2, 3x USB 3.2 Gen 1 Type-A, 1x HDMI 2.0b, 1x 3.5mm Audio Jack, 1x RJ45 LAN Jack, 1x Keystone\",\"Hệ điều hành\":\"Windows 10 Home\",\"Pin\":\"4 Cell 90WHrs\",\"Kích thước\":\"35.4 x 25.9 x 2.26 cm\",\"Trọng lượng\":\"2.3 kg\",\"Công nghệ màn hình\":\"Chất lượng màn hính NanoEdge, Độ bao phủ màu DCI-P3:100.00%\",\"Màn hình cảm ứng\":\"Không\",\"Chất liệu tấm nền\":\"Tấm nền IPS\",\"Độ phân giải\":\"2K (Quad HD)\",\"Wi-Fi\":\"Wi-Fi 6 802.11ax (2x2)\",\"Bluetooth\":\"v5.1\",\"Khe đọc thẻ nhớ\":\"Có\",\"Tính năng đặc biệt\":\"Ổ cứng SSD, Wi-Fi 6\"}', 1, 2, 4, 1, '2021-07-15 17:27:45', '2021-07-17 11:54:42', NULL);
INSERT INTO `products` VALUES (569, 'Laptop Gaming Acer Nitro 5 AN515-45-R3SM', 'laptop-gaming-acer-nitro-5-an515-45-r3sm', 60, 15000000, 21290000, 0, '<div class=\"blog-content\">\r\n<h2 style=\"text-align:justify\"><strong>Laptop Acer Nitro 5 AN515-45 - Laptop gaming h&acirc;̀m h&ocirc;́ với hi&ecirc;̣u năng vượt tr&ocirc;̣i</strong></h2>\r\n\r\n<p style=\"text-align:justify\"><em>Các game thủ hãy khai phá và chinh phục mọi tựa game hi&ecirc;̣n nay với </em><em><strong><a href=\"https://cellphones.com.vn/laptop/acer/nitro.html\" target=\"_self\" title=\"Laptop Acer Nitro chính hãng\">laptop Acer Nitro</a> 5 AN515-45</strong> - chi&ecirc;́c laptop gaming với b&ocirc;̣ CPU Ryzen 5 5600H cùng đ&ocirc;̀ họa GeForce GTX 1650 sẽ mang đ&ecirc;́n sức mạnh chơi game vượt tr&ocirc;̣i.</em></p>\r\n\r\n<h3 style=\"text-align:justify\"><strong>Thi&ecirc;́t k&ecirc;́ cứng cáp với bàn phím đèn LED RGB, màn hình 15.6 inch FHD 144Hz</strong></h3>\r\n\r\n<p style=\"text-align:justify\">Laptop Acer Nitro 5 AN515-45-R3SM mang ki&ecirc;̉u dáng h&acirc;̀m h&ocirc;́ và cứng cáp đúng với ti&ecirc;u chu&acirc;̉n của m&ocirc;̣t chi&ecirc;́c laptop chơi game hi&ecirc;̣n nay. Chi&ecirc;́c máy được phủ lớp vỏ nguy&ecirc;n kh&ocirc;́i sắc đen đ&acirc;̀y huy&ecirc;̀n bí và &acirc;́n tượng ở mọi góc cạnh.</p>\r\n\r\n<p style=\"text-align:justify\">Đặc bi&ecirc;̣t, bàn phím của Acer Nitro 5 AN515-45-R3SM được lắp h&ecirc;̣ th&ocirc;́ng đèn LED RGB rực rỡ, tạo n&ecirc;n vẻ đẹp cu&ocirc;́n hút cho chi&ecirc;́c máy cũng như thu&acirc;̣n ti&ecirc;̣n đ&ecirc;̉ game thủ quan sát phím trong m&ocirc;i trường t&ocirc;́i.</p>\r\n\r\n<p style=\"text-align:justify\"><img alt=\"Thiết kế cứng cáp với bàn phím đèn LED RGB, màn hình 15.6 inch FHD 144Hz\" class=\"cpslazy loaded\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/laptop/laptop-gaming-acer-nitro-5-an515-45-r3sm-r5-5600h-gtx1650-1.jpg\" style=\"height:600px; width:100%\" /></p>\r\n\r\n<p style=\"text-align:justify\">Acer Nitro 5 AN515-45-R3SM cường hóa trải nghi&ecirc;̣m gaming cho mọi game thủ với màn hình 15.6 inch Full HD. Kh&ocirc;ng chỉ mang thi&ecirc;́t k&ecirc;́ vi&ecirc;̀n bezel mỏng, màn hình 15.6 inch tr&ecirc;n máy còn h&ocirc;̃ trợ tính năng Acer ComfyView giúp game thủ quan sát màn hình kh&ocirc;ng bị mỏi mắt trong thời gian dài. T&ocirc;́c đ&ocirc;̣ làm tươi 144 Hz sẽ giúp hình ảnh chuy&ecirc;̉n đ&ocirc;̣ng mượt mà hơn và ch&acirc;n thực hơn tr&ecirc;n từng tựa game.</p>\r\n\r\n<h3 style=\"text-align:justify\"><strong>Vi xử lý Ryzen 5 5600H cùng đ&ocirc;̀ họa GeForce GTX 1650 đáp ứng cả những tựa game nặng</strong></h3>\r\n\r\n<p style=\"text-align:justify\">Với Acer Nitro 5 AN515-45, game thủ có th&ecirc;̉ thỏa sức chinh chi&ecirc;́n tựa game y&ecirc;u thích với thi&ecirc;́t l&acirc;̣p đ&ocirc;̀ họa t&ocirc;́i đa m&ocirc;̣t cách thoải mái nh&acirc;́t. Chi&ecirc;́c laptop gaming này được trang bị CPU Ryzen 5 5600H, với 6 nh&acirc;n 12 lu&ocirc;̀ng t&acirc;̀n s&ocirc;́ 3.3 GHz ~ 4.2 GHz sẽ đảm bảo các ph&acirc;̀n m&ecirc;̀m trò chơi l&acirc;̃n ph&acirc;̀n m&ecirc;̀m c&ocirc;ng vi&ecirc;̣c v&acirc;̣n hành mượt mà và hi&ecirc;̣u quả.</p>\r\n\r\n<p style=\"text-align:justify\">Đ&ocirc;̀ng hành cùng Ryzen 5 5600H là card đ&ocirc;̀ họa NVIDIA GeForce GTX 1650 4GB. Đ&acirc;y là chi&ecirc;́c card đ&ocirc;̀ họa chưa bao giờ l&ocirc;̃i thời bởi khả năng render đ&ocirc;̀ họa mượt mà, mang đ&ecirc;́n hình ảnh 3D ch&acirc;n thực kh&ocirc;ng chỉ cho những tựa game h&acirc;́p d&acirc;̃n, mà còn phục vụ t&ocirc;́t những c&ocirc;ng vi&ecirc;̣c thi&ecirc;́t k&ecirc;́ chuy&ecirc;n s&acirc;u.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">Ngoài ra, laptop gaming Acer Nitro 5 AN515-45 còn trang bị th&ecirc;m mức RAM 8 GB &nbsp;và &ocirc;̉ cứng SSD dung lượng 512 GB. Và với b&ocirc;̣ pin 4 cell 57Whr, game thủ sẽ có khoảng thời gian chi&ecirc;́n game dài l&acirc;u và thoải mái, kh&ocirc;ng phải lo nghĩ đ&ecirc;́n thời gian sạc pin.</p>\r\n</div>', '{\"Loại CPU\":\"AMD Ryzen 9 5900HX 3.3GHz up to 4.6GHz 16MB\",\"Loại card đồ họa\":\"NVIDIA GeForce RTX 3070 8GB GDDR6\",\"Dung lượng RAM\":\"16GB\",\"Loại RAM\":\"DDR4 3200Mhz, 2 khe cắm, hỗ trợ tối đa 32GB\",\"Ổ cứng\":\"1TB SSD PCIE G3X4 (2 slot)\",\"Kích thước màn hình\":\"15.6 inches\",\"Độ phân giải màn hình\":\"2560x1440 (Quad HD)\",\"Cổng giao tiếp\":\"1x Type C USB 3.2 Gen 2, 3x USB 3.2 Gen 1 Type-A, 1x HDMI 2.0b, 1x 3.5mm Audio Jack, 1x RJ45 LAN Jack, 1x Keystone\",\"Hệ điều hành\":\"Windows 10 Home\",\"Pin\":\"4 Cell 90WHrs\",\"Kích thước\":\"35.4 x 25.9 x 2.26 cm\",\"Trọng lượng\":\"2.3 kg\",\"Công nghệ màn hình\":\"Chất lượng màn hính NanoEdge, Độ bao phủ màu DCI-P3:100.00%\",\"Màn hình cảm ứng\":\"Không\",\"Chất liệu tấm nền\":\"Tấm nền IPS\",\"Độ phân giải\":\"2K (Quad HD)\",\"Wi-Fi\":\"Wi-Fi 6 802.11ax (2x2)\",\"Bluetooth\":\"v5.1\",\"Khe đọc thẻ nhớ\":\"Có\",\"Tính năng đặc biệt\":\"Ổ cứng SSD, Wi-Fi 6\"}', 1, 2, 18, 1, '2021-07-15 17:30:42', '2021-07-17 11:54:42', NULL);
INSERT INTO `products` VALUES (570, 'Laptop Acer Aspire 3 A315-55G-504M', 'laptop-acer-aspire-3-a315-55g-504m', 60, 10000000, 13890000, 0, '<p><strong><a href=\"https://cellphones.com.vn/laptop/acer/aspire.html\" target=\"_blank\" title=\"Laptop Acer Aspire\">Laptop Acer Aspire</a>&nbsp;3 A315-55G-504M NX.HNSSV.006</strong>&nbsp;l&agrave; một trong những d&ograve;ng laptop b&aacute;n chạy h&agrave;ng đầu. Chiếc laptop mang trong m&igrave;nh thiết kế v&ocirc; c&ugrave;ng thời trang, thời thượng. C&ugrave;ng với cấu h&igrave;nh mạnh mẽ, hiệu quả hoạt động tốt, chiếc m&aacute;y n&agrave;y đ&atilde; trở th&agrave;nh sự lựa chọn v&ocirc; c&ugrave;ng hiệu quả của người ti&ecirc;u d&ugrave;ng tại Việt Nam.</p>\r\n\r\n<p>C&ugrave;ng kh&aacute;m ph&aacute; th&ecirc;m mẫu<a href=\"https://cellphones.com.vn/laptop-acer-aspire-3-a315.html\" target=\"_blank\" title=\"laptop Acer Aspire 3 A315-56-502X NX.HS5SV.00F\">&nbsp;laptop Acer Aspire 3 A315-56-502X NX.HS5SV.00F</a>&nbsp;đang được b&aacute;n chạy tại hệ thống CellphoneS nh&eacute;!</p>', '{\"Loại CPU\":\"AMD Ryzen 9 5900HX 3.3GHz up to 4.6GHz 16MB\",\"Loại card đồ họa\":\"NVIDIA GeForce RTX 3070 8GB GDDR6\",\"Dung lượng RAM\":\"16GB\",\"Loại RAM\":\"DDR4 3200Mhz, 2 khe cắm, hỗ trợ tối đa 32GB\",\"Ổ cứng\":\"1TB SSD PCIE G3X4 (2 slot)\",\"Kích thước màn hình\":\"15.6 inches\",\"Độ phân giải màn hình\":\"2560x1440 (Quad HD)\",\"Cổng giao tiếp\":\"1x Type C USB 3.2 Gen 2, 3x USB 3.2 Gen 1 Type-A, 1x HDMI 2.0b, 1x 3.5mm Audio Jack, 1x RJ45 LAN Jack, 1x Keystone\",\"Hệ điều hành\":\"Windows 10 Home\",\"Pin\":\"4 Cell 90WHrs\",\"Kích thước\":\"35.4 x 25.9 x 2.26 cm\",\"Trọng lượng\":\"2.3 kg\",\"Công nghệ màn hình\":\"Chất lượng màn hính NanoEdge, Độ bao phủ màu DCI-P3:100.00%\",\"Màn hình cảm ứng\":\"Không\",\"Chất liệu tấm nền\":\"Tấm nền IPS\",\"Độ phân giải\":\"2K (Quad HD)\",\"Wi-Fi\":\"Wi-Fi 6 802.11ax (2x2)\",\"Bluetooth\":\"v5.1\",\"Khe đọc thẻ nhớ\":\"Có\",\"Tính năng đặc biệt\":\"Ổ cứng SSD, Wi-Fi 6\"}', 1, 2, 18, 1, '2