INSERT INTO `products` VALUES (575, 'Laptop Lenovo ThinkBook 15 G2 ITL', 'laptop-lenovo-thinkbook-15-g2-itl', 110, 12000000, 15490000, 0, '<div class=\"blog-content\">\r\n<h2 style=\"text-align:justify\"><strong>Laptop Lenovo ThinkBook 15 G2 ITL thiết kế thanh lịch, chất lượng ổn định</strong></h2>\r\n\r\n<p style=\"text-align:justify\"><a href=\"https://cellphones.com.vn/laptop/lenovo/thinkpad.html\" target=\"_blank\">ThinkPad </a>l&agrave; d&ograve;ng sản phẩm hướng tới đối tượng doanh nh&acirc;n của Lenovo. Tất cả c&aacute;c thiết kế thuộc d&ograve;ng n&agrave;y đều mang t&iacute;nh thanh lịch, sang trọng, cấu h&igrave;nh ổn định to&aacute;t l&ecirc;n thần th&aacute;i của một doanh nh&acirc;n. <strong>Laptop Lenovo Thinkbook 155 G2 ITL</strong> l&agrave; d&ograve;ng sản phẩm thế hệ mới mang đến cho người d&ugrave;ng những trải nghiệm kh&aacute;c biệt.</p>\r\n\r\n<h3 style=\"text-align:justify\"><strong>Thiết kế chuy&ecirc;n nghiệp, m&agrave;n h&igrave;nh Full HD</strong></h3>\r\n\r\n<p style=\"text-align:justify\">Laptop Lenovo Thinkbook 15 G2 ITL sở hữu thiết kế chuy&ecirc;n nghiệp mang đến cho người d&ugrave;ng nhiều cảm hứng l&agrave;m việc. Phần viền m&agrave;n h&igrave;nh si&ecirc;u mỏng, k&iacute;ch thước nhỏ gọn kh&ocirc;ng chỉ mang đến phong c&aacute;ch độc đ&aacute;o m&agrave; c&ograve;n gi&uacute;p bạn dễ d&agrave;ng mang đi bất cứ đ&acirc;u.</p>\r\n\r\n<p style=\"text-align:justify\"><img alt=\"Thiết kế chuyên nghiệp, màn hình Full HD\" class=\"cpslazy loaded\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/laptop/Laptop-lenovo-thinkbook-15-g2-itl-1.jpg\" style=\"height:600px; width:100%\" /></p>\r\n\r\n<p style=\"text-align:justify\">M&agrave;n h&igrave;nh của m&aacute;y c&oacute; k&iacute;ch thước 15,6 inch tương đối lớn, độ ph&acirc;n giải Full HD sắc n&eacute;t v&agrave; gam m&agrave;u rộng mang đến h&igrave;nh ảnh sống động v&agrave; m&agrave;u sắc ch&acirc;n thật. Ngo&agrave;i ra, c&ocirc;ng nghệ lọc &aacute;nh s&aacute;ng xanh tr&ecirc;n m&aacute;y gi&uacute;p bảo vệ mắt để c&oacute; thể l&agrave;m việc thoải m&aacute;i trong thời gian d&agrave;i.</p>\r\n\r\n<h3 style=\"text-align:justify\"><strong>Cấu h&igrave;nh ti&ecirc;n tiến, khả năng xử l&yacute; đồ họa vượt trội</strong></h3>\r\n\r\n<p style=\"text-align:justify\">Lenovo Thinkbook 155 G2 ITL được trang bị bộ vi xử l&yacute; Intel thế hệ thứ 11 mang đến sức mạnh vượt trội. Chip Intel Core i5 1135G7 với 4 l&otilde;i 8 luồng, tốc độ xung nhịp tối đa 4.20GHz được sản xuất tr&ecirc;n tiến tr&igrave;nh 10nm SuperFin kh&ocirc;ng chỉ mạnh mẽ m&agrave; c&ograve;n tiết kiệm điện. RAM 8GB v&agrave; ổ cứng SSD 512GB gi&uacute;p bạn tối ưu hiệu suất c&ocirc;ng việc.</p>\r\n\r\n<p style=\"text-align:justify\"><img alt=\"Cấu hình tiên tiến, khả năng xử lý đồ họa vượt trội\" class=\"cpslazy loaded\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/laptop/Laptop-lenovo-thinkbook-15-g2-itl-2.jpg\" style=\"height:600px; width:100%\" /></p>\r\n\r\n<p style=\"text-align:justify\">Ngo&agrave;i ra, GPU đồ họa Intel Iris Xe mạnh mẽ h&agrave;ng đầu gi&uacute;p bạn sử dụng c&aacute;c ứng dụng đồ họa một c&aacute;ch mượt m&agrave;.</p>\r\n\r\n<h3 style=\"text-align:justify\"><strong>Bảo mật th&ocirc;ng minh, kết nối ấn tượng</strong></h3>\r\n\r\n<p style=\"text-align:justify\">Laptop Lenovo Thinkbook 155 G2 ITL sở hữu nền tảng TPM mang đến khả năng bảo mật th&ocirc;ng minh ngăn chặn c&aacute;c trường hợp truy cập tr&aacute;i ph&eacute;p. Nhiều t&iacute;nh năng th&ocirc;ng minh kh&aacute;c như: gấp mở để mở m&aacute;y, l&agrave;m m&aacute;t th&ocirc;ng minh,... được trang bị để mang đến trải nghiệm ho&agrave;n hảo nhất.</p>\r\n\r\n<p style=\"text-align:justify\">&nbsp;</p>\r\n\r\n<p style=\"text-align:justify\">M&aacute;y c&oacute; khả năng kết nối ấn tượng nhờ c&ocirc;ng nghệ kết nối ti&ecirc;n tiến như: Thunderbolt 4, HDMI, USB-C 3.2 Gen 2,USB 3.2 Gen 1. Chuẩn WiFi 6 cho tốc độ nhanh hơn v&agrave; vượt trội hơn.</p>\r\n</div>', '{\"Loại CPU\":\"AMD Ryzen 9 5900HX 3.3GHz up to 4.6GHz 16MB\",\"Loại card đồ họa\":\"NVIDIA GeForce RTX 3070 8GB GDDR6\",\"Dung lượng RAM\":\"16GB\",\"Loại RAM\":\"DDR4 3200Mhz, 2 khe cắm, hỗ trợ tối đa 32GB\",\"Ổ cứng\":\"1TB SSD PCIE G3X4 (2 slot)\",\"Kích thước màn hình\":\"15.6 inches\",\"Độ phân giải màn hình\":\"2560x1440 (Quad HD)\",\"Cổng giao tiếp\":\"1x Type C USB 3.2 Gen 2, 3x USB 3.2 Gen 1 Type-A, 1x HDMI 2.0b, 1x 3.5mm Audio Jack, 1x RJ45 LAN Jack, 1x Keystone\",\"Hệ điều hành\":\"Windows 10 Home\",\"Pin\":\"4 Cell 90WHrs\",\"Kích thước\":\"35.4 x 25.9 x 2.26 cm\",\"Trọng lượng\":\"2.3 kg\",\"Công nghệ màn hình\":\"Chất lượng màn hính NanoEdge, Độ bao phủ màu DCI-P3:100.00%\",\"Màn hình cảm ứng\":\"Không\",\"Chất liệu tấm nền\":\"Tấm nền IPS\",\"Độ phân giải\":\"2K (Quad HD)\",\"Wi-Fi\":\"Wi-Fi 6 802.11ax (2x2)\",\"Bluetooth\":\"v5.1\",\"Khe đọc thẻ nhớ\":\"Có\",\"Tính năng đặc biệt\":\"Ổ cứng SSD, Wi-Fi 6\"}', 1, 2, 24, 1, '2021-07-15 17:50:42', '2021-07-17 11:54:46', NULL);
INSERT INTO `products` VALUES (576, 'Laptop Lenovo Ideapad Slim 3 14IIL05', 'laptop-lenovo-ideapad-slim-3-14iil05', 110, 5000000, 9980000, 0, '<div class=\"blog-content\">\r\n<h2 style=\"text-align:justify\"><strong>Laptop Lenovo IdeaPad Slim 3 14IIL05 81WD00VJVN - Laptop c&ocirc;ng sở mỏng gọn cho nhu c&acirc;̀u làm vi&ecirc;̣c ti&ecirc;̣n lợi</strong></h2>\r\n\r\n<p style=\"text-align:justify\"><em>Thi&ecirc;́t k&ecirc;́ gọn nhẹ, ki&ecirc;̉u dáng thanh lịch, chứa đựng hi&ecirc;̣u năng &ocirc;̉n định cho nhu c&acirc;̀u làm vi&ecirc;̣c và học t&acirc;̣p, </em><em><strong><a href=\"https://cellphones.com.vn/laptop/lenovo.html\" title=\"Laptop Lenovo\">laptop Lenovo</a> IdeaPad Slim 3 14IIL05 81WD00VJVN</strong> chính là m&acirc;̃u laptop c&ocirc;ng sở phù hợp cho mọi đ&ocirc;́i tượng người dùng.</em></p>\r\n\r\n<h3 style=\"text-align:justify\"><strong>Thi&ecirc;́t k&ecirc;́ gọn nhẹ, thanh lịch với màn hình 14 inch FHD</strong></h3>\r\n\r\n<p style=\"text-align:justify\">Laptop Lenovo IdeaPad Slim 3 14IIL05 81WD00VJVN được thi&ecirc;́t k&ecirc;́ dành cho nhu c&acirc;̀u làm vi&ecirc;̣c di chuy&ecirc;̉n thường xuy&ecirc;n. Chi&ecirc;́c máy có ki&ecirc;̉u dáng thanh lịch với lớp vỏ màu xám đẹp mắt, phù hợp với m&ocirc;i trường học t&acirc;̣p và c&ocirc;ng sở. Thi&ecirc;́t k&ecirc;́ gọn gàng, mỏng, và nhẹ chỉ 1.6kg giúp cho Lenovo IdeaPad Slim 3 14IIL05 81WD00VJVN trở thành lựa chọn tuy&ecirc;̣t vời làm laptop c&ocirc;ng vi&ecirc;̣c ti&ecirc;̣n lợi.</p>\r\n\r\n<p style=\"text-align:justify\"><img alt=\"Laptop Lenovo IdeaPad Slim 3 14IIL05 \" class=\"cpslazy loaded\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/laptop/laptop-lenovo-ideapad-slim-3-14iil05-81wd00vjvn-1.jpg\" style=\"height:600px; width:100%\" /></p>\r\n\r\n<p style=\"text-align:justify\">Laptop Lenovo IdeaPad Slim 3 14IIL05 81WD00VJVN trang bị màn hình có kích thước 14 inch với đ&ocirc;̣ ph&acirc;n giải Full HD (1920 x 1080), giúp cho hình ảnh hi&ecirc;̉n thị rõ nét và phù hợp với tác vụ đa nhi&ecirc;̣m. Đặc bi&ecirc;̣t, màn hình này còn có khả năng ch&ocirc;́ng chói, đảm bảo bạn lu&ocirc;n quan sát rõ từng chi ti&ecirc;́t khi ở trong đi&ecirc;̀u ki&ecirc;̣n thừa sáng.</p>\r\n\r\n<h3 style=\"text-align:justify\"><strong>Vi xử lý Intel Core i3-1005G1 cùng &ocirc;̉ cứng SSD 256GB mang lại t&ocirc;́c đ&ocirc;̣ xử lý nhanh chóng</strong></h3>\r\n\r\n<p style=\"text-align:justify\">Laptop Lenovo IdeaPad Slim 3 14IIL05 81WD00VJVN được trang bị b&ocirc;̣ vi xử lý Intel Core i3-1005G1, với 2 nh&acirc;n 4 lu&ocirc;̀ng t&ocirc;́c đ&ocirc;̣ xung nhịp 1.2 GHz ~ 3.4 GHz giúp tăng t&ocirc;́c đ&ocirc;̣ xử lý ph&acirc;̀n m&ecirc;̀m của máy, đảm bảo bạn làm vi&ecirc;̣c đa nhi&ecirc;̣m m&ocirc;̣t cách hi&ecirc;̣u quả. Chi&ecirc;́c máy còn được trang bị RAM 4GB và &ocirc;̉ cứng SSD dung lượng 256GB cho khả năng làm vi&ecirc;̣c t&ocirc;́t hơn.</p>\r\n\r\n<p style=\"text-align:justify\"><img alt=\"Laptop Lenovo IdeaPad Slim 3 14IIL05 \" class=\"cpslazy loaded\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/laptop/laptop-lenovo-ideapad-slim-3-14iil05-81wd00vjvn-2.jpg\" style=\"height:600px; width:100%\" /></p>\r\n\r\n<p style=\"text-align:justify\">Laptop Lenovo IdeaPad Slim 3 14IIL05 81WD00VJVN được tích hợp sẵn h&ecirc;̣ đi&ecirc;̀u hành Windows 10 Home. Xung quanh máy được trang bị các chu&acirc;̉n k&ecirc;́t n&ocirc;́i đáp ứng c&ocirc;ng vi&ecirc;̣c của bạn như HDMI, USB 2.0, USB 3.2, và cả jack tai nghe cho nhu c&acirc;̀u sử dụng tai nghe có d&acirc;y. Vi&ecirc;n pin 2 cell 35Wh sẽ đảm bảo máy hoạt đ&ocirc;̣ng trong thời gian dài, giúp bạn y&ecirc;n t&acirc;m xử lý c&ocirc;ng vi&ecirc;̣c mọi lúc mọi nơi.</p>\r\n</div>', '{\"Loại CPU\":\"AMD Ryzen 9 5900HX 3.3GHz up to 4.6GHz 16MB\",\"Loại card đồ họa\":\"NVIDIA GeForce RTX 3070 8GB GDDR6\",\"Dung lượng RAM\":\"16GB\",\"Loại RAM\":\"DDR4 3200Mhz, 2 khe cắm, hỗ trợ tối đa 32GB\",\"Ổ cứng\":\"1TB SSD PCIE G3X4 (2 slot)\",\"Kích thước màn hình\":\"15.6 inches\",\"Độ phân giải màn hình\":\"2560x1440 (Quad HD)\",\"Cổng giao tiếp\":\"1x Type C USB 3.2 Gen 2, 3x USB 3.2 Gen 1 Type-A, 1x HDMI 2.0b, 1x 3.5mm Audio Jack, 1x RJ45 LAN Jack, 1x Keystone\",\"Hệ điều hành\":\"Windows 10 Home\",\"Pin\":\"4 Cell 90WHrs\",\"Kích thước\":\"35.4 x 25.9 x 2.26 cm\",\"Trọng lượng\":\"2.3 kg\",\"Công nghệ màn hình\":\"Chất lượng màn hính NanoEdge, Độ bao phủ màu DCI-P3:100.00%\",\"Màn hình cảm ứng\":\"Không\",\"Chất liệu tấm nền\":\"Tấm nền IPS\",\"Độ phân giải\":\"2K (Quad HD)\",\"Wi-Fi\":\"Wi-Fi 6 802.11ax (2x2)\",\"Bluetooth\":\"v5.1\",\"Khe đọc thẻ nhớ\":\"Có\",\"Tính năng đặc biệt\":\"Ổ cứng SSD, Wi-Fi 6\"}', 1, 2, 24, 1, '2021-07-15 17:54:44', '2021-07-17 11:54:46', NULL);
INSERT INTO `products` VALUES (577, 'Pin sạc dự phòng Xiaomi Redmi 20000mah', 'pin-sac-du-phong-xiaomi-redmi-20000mah', 160, 200000, 450000, 0, '<div class=\"blog-content\">\r\n<h2>Pin dự ph&ograve;ng Xiaomi Redmi 20000mAh &ndash; Phụ kiện pin sạc an to&agrave;n, hiệu suất cao</h2>\r\n\r\n<p><em>Xiaomi l&agrave; thương hiệu vốn nổi tiếng với nhiều người ti&ecirc;u d&ugrave;ng kh&ocirc;ng chỉ bởi những chiếc điện thoại chất lượng, cấu h&igrave;nh cao gi&aacute; rẻ m&agrave; c&ograve;n những phụ kiện pin dự ph&ograve;ng cũng được nhiều người tin d&ugrave;ng. Dung lượng 20000mAh, c&ugrave;ng với khả năng sạc nhanh 18W th&igrave; <strong>pin sạc dự ph&ograve;ng Xiaomi Redmi 20000mAh</strong> sạc nhanh 18W l&agrave; một lựa chọn hợp l&yacute; v&agrave; hấp dẫn.</em></p>\r\n\r\n<h3>Thiết kế nhỏ gọn, ho&agrave;n thiện từ nhựa ABS</h3>\r\n\r\n<p>C&aacute;c sản phẩm phụ kiện của Xiaomi đều hướng đến lối thiết kế đơn giản v&agrave; nhỏ gọn, tuy nhi&ecirc;n kh&ocirc;ng k&eacute;m phần tinh tế v&agrave; pin sạc dự ph&ograve;ng Xiaomi Redmi 20000mAh cũng vậy. Thiết bị sở hữu k&iacute;ch thước tổng thể 154 x 73.6 x 27.3mm, c&oacute; thể thấy mặc d&ugrave; kh&ocirc;ng nhỏ gọn như nhiều sản phẩm cao cấp kh&aacute;c, nhưng n&oacute; chứa dung lượng pin l&ecirc;n đến 20000mAh. <a href=\"https://cellphones.com.vn/phu-kien/pin-du-phong/xiaomi.html\" target=\"_blank\" title=\"Sạc dự phòng Xiaomi chính hãng\">Sạc dự ph&ograve;ng Xiaomi</a> n&agrave;y ho&agrave;n to&agrave;n c&oacute; thể nằm gọn b&ecirc;n trong balo hay t&uacute;i x&aacute;ch để người d&ugrave;ng c&oacute; thể mang đến khắp nơi.</p>\r\n\r\n<p><img alt=\"Thiết kế nhỏ gọn, hoàn thiện từ nhựa ABS\" class=\"cpslazy loaded\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/accessories/power_bank/pin-sac-du-phong-xiaomi-redmi-20000mah-sac-nhanh-18w-1.jpg\" style=\"height:600px; width:100%\" /></p>\r\n\r\n<p>C&aacute;c chi tiết của phần vỏ đều được ho&agrave;n thiện từ nhựa ABS. Đ&acirc;y l&agrave; chất liệu đem lại trọng lượng nhẹ nhưng lại c&oacute; độ bền cao v&agrave; khả năng chống ch&aacute;y. Hơn nữa, thiết kế bề mặt hơi nh&aacute;m, để gi&uacute;p tăng cảm gi&aacute;c cầm nắm chắc chắn v&agrave; an to&agrave;n hơn. Sạc dự ph&ograve;ng Xiaomi Redmi 20000mAh được kho&aacute;c l&ecirc;n gam m&agrave;u trắng tăng th&ecirc;m t&iacute;nh trang nh&atilde; cho ngoại h&igrave;nh.</p>\r\n\r\n<h3>Vi&ecirc;n pin dung lượng lớn 20000mAh, sạc hai thiết bị c&ugrave;ng một l&uacute;c với c&ocirc;ng suất tối đa 18W</h3>\r\n\r\n<p>Dung lượng tr&ecirc;n vi&ecirc;n pin n&agrave;y đạt tới 20000mAh. Với hiệu suất chuyển đổi cao, pin sạc dự ph&ograve;ng Xiaomi Redmi 20000mAh sạc nhanh 18W c&oacute; thể sạc 4,3 lần cho Xiaomi Mi A1; 7,1 lần cho iPhone 7; 2,5 lần cho iPad Mini. Đồng thời, pin sạc n&agrave;y cũng c&oacute; thể sạc cho nhiều loại điện thoại, m&aacute;y t&iacute;nh bảng kh&aacute;c nhau, thậm ch&iacute; l&agrave; smartwatch.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Điểm thu h&uacute;t thứ hai của thiết bị n&agrave;y l&agrave; đầu v&agrave;o của pin dự ph&ograve;ng cũng kh&aacute; ấn tượng khi được trang bị c&ugrave;ng l&uacute;c cả hai giao tiếp<span style=\"color:#333300\"><span style=\"color:#333300\"> micro-USB</span></span> v&agrave; <span style=\"color:#333300\"><span style=\"color:#333300\">USB Type-C</span></span>, tạo điều kiện sạc pin thuận tiện hơn. Ngo&agrave;i ra, với hai đầu ra USB cho ph&eacute;p thiết bị c&oacute; thể sạc đồng thời hai thiết bị điện thoại, m&aacute;y t&iacute;nh bảng hay vừa điện thoại vừa m&aacute;y t&iacute;nh bảng c&ugrave;ng một l&uacute;c. Điều n&agrave;y rất dễ d&agrave;ng cho những người thường xuy&ecirc;n di chuyển với nhiều thiết bị hay chia sẻ nguồn điện sạc cho bạn b&egrave; của m&igrave;nh.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>N&oacute; cũng đi k&egrave;m với c&ocirc;ng nghệ sạc nhanh với điện &aacute;p tối đa l&agrave; 12V-1.5A, tương đương 18W, bao gồm sạc ngược, gi&uacute;p thời gian nạp lại pin nhanh hơn, tiết kiệm được thời gian cho người sử dụng. Lưu &yacute; rằng sạc nhanh 18W chỉ l&agrave;m việc tốt trong điều kiện sạc một thiết bị, khi sử dụng c&ugrave;ng l&uacute;c cả hai cổng, điện &aacute;p sẽ bị giới hạn ở mức tối đa l&agrave; 5.1V-3.6A cho cả hai cổng.</p>\r\n\r\n<h3>T&iacute;nh năng bảo vệ an to&agrave;n cho pin sạc, l&otilde;i pin Polymer ion lithium đảm bảo độ ổn định</h3>\r\n\r\n<p>Pin sạc dự ph&ograve;ng Xiaomi Redmi 20000mAh sạc nhanh 18W c&ograve;n được nh&agrave; sản xuất trang bị chip mạch chất lượng cao, kh&ocirc;ng chỉ an to&agrave;n m&agrave; c&ograve;n cải thiện tỷ lệ chuyển đổi, cung cấp d&ograve;ng điện ổn định hơn. B&ecirc;n cạnh đ&oacute;, n&oacute; c&ograve;n được trang bị c&aacute;c t&iacute;nh năng bảo vệ như bảo vệ nhiệt độ, bảo vệ ngắn mạch, bảo vệ qu&aacute; &aacute;p đầu v&agrave;o, bảo vệ đầu ra qu&aacute; d&ograve;ng, bảo vệ đầu ra qu&aacute; &aacute;p, bảo vệ pin chống qu&aacute; tải, bảo vệ phần cứng,...</p>\r\n\r\n<p>&nbsp;</p>\r\n</div>', '{\"Dòng điện vào\":\"(Micro-USB & Type-C): 5V\\/2.1A, 9V\\/2.1A, 12V\\/1.5A\",\"Dòng điện ra\":\"Khi sạc 1 cổng : 5.1V\\/2.4A, 9V\\/2A, 12V\\/1.5A. Max Khi sạc 2 cổng: 5.1V\\/3.6A\",\"Pin\":\"20000Mah\",\"Tính năng khác\":\"Tích hợp bảo vệ mạch thông minh Chế độ sạc nhỏ giọt\",\"Hãng sản xuất\":\"Xiaomi\"}', 1, 7, 19, 1, '2021-07-15 18:02:35', '2021-07-17 11:54:49', NULL);
INSERT INTO `products` VALUES (578, 'Pin sạc dự phòng Xiaomi Power Bank 3 10.000mAh', 'pin-sac-du-phong-xiaomi-power-bank-3-10000mah', 110, 300000, 590000, 0, '<div class=\"blog-content\">\r\n<h2><strong>Pin sạc dự ph&ograve;ng Xiaomi Power Bank 3 10,000mAh Ultra Compact - Pin sạc đ&aacute;ng đầu tư v&igrave; chất lượng tốt</strong></h2>\r\n\r\n<p>Sạc dự ph&ograve;ng l&agrave; thiết bị rất cần thiết cho những ai thường xuy&ecirc;n đi xa. Nếu bạn vẫn đang băn khoăn kh&ocirc;ng biết n&ecirc;n chọn loại n&agrave;o th&igrave; tại sao kh&ocirc;ng thử qua chiếc pin <a href=\"https://cellphones.com.vn/phu-kien/pin-du-phong/xiaomi.html\" target=\"_self\" title=\"Sạc dự phòng Xiaomi chính hãng\">sạc dự ph&ograve;ng Xiaomi</a> Power Bank 3 10,000mAh Ultra Compact?</p>\r\n\r\n<h3><strong>Thiết kế mỏng nhẹ, chất liệu cao cấp</strong></h3>\r\n\r\n<p>Về thiết kế, pin sạc dự ph&ograve;ng Xiaomi được thiết kế rất mỏng, trọng lượng nhẹ rất th&iacute;ch hợp để người d&ugrave;ng c&oacute; thể mang đi trong những chuyến đi xa d&agrave;i ng&agrave;y v&igrave; sạc sẽ kh&ocirc;ng chiếm qu&aacute; nhiều diện t&iacute;ch trong h&agrave;nh l&yacute; của bạn.</p>\r\n\r\n<p><img alt=\"Thiết kế mỏng nhẹ, chất liệu cao cấp\" class=\"cpslazy loaded\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/accessories/charger/Pin-sac-du-phong-Xiaomi-Power-Bank-3-10_000mAh-Ultra-Compact.JPG\" style=\"height:600px; width:100%\" /></p>\r\n\r\n<p>Về chất liệu, pin sạc được l&agrave;m từ vật liệu cao cấp c&oacute; khả năng chịu lực tốt. Ngo&agrave;i ra, chất liệu tốt c&ograve;n c&oacute; khả năng chống lại sự thay đổi nhiệt độ đột ngột, hiện tượng đoản mạch hay qu&aacute; nhiệt trong qu&aacute; tr&igrave;nh sử dụng.</p>\r\n\r\n<h3><strong>C&ocirc;ng nghệ sạc nhanh tối ưu, sạc nhiều thiết bị c&ugrave;ng l&uacute;c</strong></h3>\r\n\r\n<p>Pin sạc Xiaomi Power Bank 3 được trang bị c&ocirc;ng nghệ sạc nhanh thế hệ mới 3.0 c&ugrave;ng Power Delivery gi&uacute;p bạn c&oacute; thể sạc thiết bị được nhanh hơn.</p>\r\n\r\n<p>Bạn sẽ sạc c&aacute;c thiết bị một c&aacute;ch dễ d&agrave;ng nhờ v&agrave;o c&ocirc;ng suất cực đại 22,5W.</p>\r\n\r\n<p><img alt=\"Công nghệ sạc nhanh tối ưu, sạc nhiều thiết bị cùng lúc\" class=\"cpslazy loaded\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/accessories/charger/Pin-sac-du-phong-Xiaomi-Power-Bank-3-10_000mAh-Ultra-Compact-1.jpg\" style=\"height:600px; width:100%\" /></p>\r\n\r\n<p>Với sạc Xiaomi Power Bank 3, bạn c&oacute; thể sạc được c&ugrave;ng l&uacute;c ba thiết bị v&igrave; pin sạc được trang bị một cổng USB-A v&agrave; hai cổng USB-C chất lượng cao, gi&uacute;p người d&ugrave;ng thoải m&aacute;i cắm sạc v&agrave; tiết kiệm được nhiều thời gian.</p>\r\n</div>', '{\"Dòng điện vào\":\"(Micro-USB & Type-C): 5V\\/2.1A, 9V\\/2.1A, 12V\\/1.5A\",\"Dòng điện ra\":\"Khi sạc 1 cổng : 5.1V\\/2.4A, 9V\\/2A, 12V\\/1.5A. Max Khi sạc 2 cổng: 5.1V\\/3.6A\",\"Pin\":\"20000Mah\",\"Tính năng khác\":\"Tích hợp bảo vệ mạch thông minh Chế độ sạc nhỏ giọt\",\"Hãng sản xuất\":\"Xiaomi\"}', 1, 7, 19, 1, '2021-07-15 19:58:32', '2021-07-17 11:54:49', NULL);
INSERT INTO `products` VALUES (579, 'Pin sạc dự phòng Xiaomi Essential 10.000mAh', 'pin-sac-du-phong-xiaomi-essential-10000mah', 110, 300000, 550000, 0, '<div class=\"blog-content\">\r\n<h2><strong>Pin sạc dự ph&ograve;ng Xiaomi Essential </strong><strong>- t&iacute;ch hợp sạc kh&ocirc;ng d&acirc;y 10W</strong><strong> c&ugrave;ng dung lượng pin lớn</strong></h2>\r\n\r\n<p>Pin sạc dự ph&ograve;ng l&agrave; một trong những phụ kiện h&agrave;ng đầu của người d&ugrave;ng điện thoại th&ocirc;ng minh. Pin sạc với khả năng bổ sung năng lượng cho điện thoại c&ugrave;ng t&iacute;nh di động cao. L&uacute;c n&agrave;y, <strong>sạc dự ph&ograve;ng Xiaomi Essential t&iacute;ch hợp sạc kh&ocirc;ng d&acirc;y 10W</strong> với thiết kế nhỏ gọn, dung lượng pin lớn sẽ l&agrave; một lựa chọn ph&ugrave; hợp.</p>\r\n\r\n<h3><strong>Vi&ecirc;n pin nhỏ gọn, độ bền cao</strong></h3>\r\n\r\n<p><a href=\"https://cellphones.com.vn/phu-kien/pin-du-phong/xiaomi.html\" target=\"_self\" title=\"Pin dự phòng Xiaomi chính hãng\">Pin sạc dự ph&ograve;ng Xiaomi</a> Essential được thiết kế dạng h&igrave;nh chữ nhật với c&aacute;c cạnh bo tr&ograve;n quen thuộc. Kết hợp với lớn vỏ nh&aacute;m chống trơn trượt, ma s&aacute;t nhờ đ&oacute; người d&ugrave;ng c&oacute; thể dễ d&agrave;ng v&agrave; thoải m&aacute;i khi cầm nắm.</p>\r\n\r\n<p>Pin sở hữu trọng lượng nhẹ, chỉ khoảng 310g. Với trọng lượng n&agrave;y, người d&ugrave;ng c&oacute; thể thoải m&aacute;i mang theo du lịch, sử dụng m&agrave; kh&ocirc;ng gặp nhiều kh&oacute; khăn hay vướng v&iacute;u.</p>\r\n\r\n<p><img alt=\"Viên pin nhỏ gọn, độ bền cao\" class=\"cpslazy loaded\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/accessories/power_bank/xiaomi-essential-10000mah-sac-khong-day-10w-4.jpg\" style=\"height:600px; width:100%\" /></p>\r\n\r\n<p>Pin với lớp vỏ cứng c&aacute;p, người d&ugrave;ng kh&ocirc;ng cần qu&aacute; lo lắng pin bị biến dạng, m&oacute;p m&eacute;o khi sử dụng. Pin dự ph&ograve;ng Xiaomi Essential được trang bị hệ thống đ&egrave;n led gi&uacute;p người d&ugrave;ng c&oacute; thể dễ d&agrave;ng nhận biết, theo d&otilde;i t&igrave;nh trạng pin. Với 4 nấc m&agrave;u trắng, lần lượt l&agrave; 25%, 50%, 75% v&agrave; 100%. Ngo&agrave;i ra, sạc cũng c&oacute; th&ecirc;m một nấc đ&egrave;n xanh th&ocirc;ng b&aacute;o t&igrave;nh trạng sạc kh&ocirc;ng d&acirc;y.</p>\r\n\r\n<h3><strong>Dung lượng pin lớn, t&iacute;ch hợp sạc kh&ocirc;ng d&acirc;y</strong></h3>\r\n\r\n<p>Pin sạc dự ph&ograve;ng Xiaomi Essential Type-C t&iacute;ch hợp sạc kh&ocirc;ng d&acirc;y 10W được trang bị dung lượng pin lớn l&ecirc;n đến 10000 mah. Với dung lượng pin lớn n&agrave;y, người d&ugrave;ng c&oacute; thể thoải m&aacute;i sử dụng m&agrave; kh&ocirc;ng sợ hết pin.</p>\r\n\r\n<p>Pin cũng được trang bị t&iacute;nh năng sạc kh&ocirc;ng d&acirc;y chuẩn Qi th&ocirc;ng minh với c&ocirc;ng suất đến 10W cho c&aacute;c thiết bị Android. Th&ocirc;ng số n&agrave;y thấp hơn, chỉ v&agrave;o khoảng 5W-7W khi sạc cho thiết bị iOS.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Sạc với hai cổng kết nối, 1 đầu ra USB-A v&agrave; đầu v&agrave;o USB-C cho ph&eacute;p sạc với d&ograve;ng điện 5V - 3A gi&uacute;p bổ sung năng lượng pin nhanh, sạc đầy chỉ trong v&ograve;ng 6 giờ. Ngo&agrave;i ra, hai cổng kết nối c&ograve;n hỗ trợ sạc nhanh gi&uacute;p tiết kiệm tối đa thời gian sạc.</p>\r\n\r\n<h3><strong>Được trang bị đầy đủ c&aacute;c cơ chế bảo vệ, an to&agrave;n khi sử dụng</strong></h3>\r\n\r\n<p>Pin sạc dự ph&ograve;ng Xiaomi Essential 10.000mAh Type-C t&iacute;ch hợp sạc kh&ocirc;ng d&acirc;y 10W sở hữu vi mạnh với 9 lớp bảo vệ với nhiều t&iacute;nh năng như: bảo vệ điện &aacute;p đầu v&agrave;o, điện &aacute;p đầu ra, qu&aacute; nhiệt, đoản mạch, bảo vệ sạc &ndash; xả qu&aacute; mức,... gi&uacute;p vảo vệ thiết bị v&agrave; người d&ugrave;ng khi sạc.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Sạc dự ph&ograve;ng với l&otilde;i pin Li-Polymer an to&agrave;n khi sử dụng. Sạc với khả năng nhận diện được thiết bị kết nối một c&aacute;ch nhanh ch&oacute;ng với phạm vi cảm biến &le; 5mm. Sạc dự ph&ograve;ng cũng tương th&iacute;ch với nhiều thiết bị, phụ kiện kh&aacute;c nhau như Samsung, iPhone, Huawei,...</p>\r\n</div>', '{\"Dòng điện vào\":\"(Micro-USB & Type-C): 5V\\/2.1A, 9V\\/2.1A, 12V\\/1.5A\",\"Dòng điện ra\":\"Khi sạc 1 cổng : 5.1V\\/2.4A, 9V\\/2A, 12V\\/1.5A. Max Khi sạc 2 cổng: 5.1V\\/3.6A\",\"Pin\":\"20000Mah\",\"Tính năng khác\":\"Tích hợp bảo vệ mạch thông minh Chế độ sạc nhỏ giọt\",\"Hãng sản xuất\":\"Xiaomi\"}', 1, 7, 19, 1, '2021-07-15 19:59:24', '2021-07-17 11:54:49', NULL);
INSERT INTO `products` VALUES (580, 'Pin dự phòng AUKEY PB-XD26 26800mAh', 'pin-du-phong-aukey-pb-xd26-26800mah', 110, 700000, 1390000, 0, '<div class=\"blog-content\">\r\n<h2><strong><a href=\"https://cellphones.com.vn/pin-du-phong-aukey-pb-xd26-26800-mah-usb-c-quick-charge-3-0-black.html\" target=\"_blank\" title=\"Pin dự phòng AUKEY PB-XD26 26800mAh USB-C Quick Charge 3.0 Đen\">Sạc dự ph&ograve;ng Aukey PB-XD26 26800mAh</a> &ndash; Nạp đầy năng lượng nhanh ch&oacute;ng cho điện thoại</strong></h2>\r\n\r\n<p><em>D&ugrave; bạn đang sử dụng bất cứ thiết bị di động n&agrave;o, c&oacute; dung lượng pin l&agrave; bao nhi&ecirc;u, <strong>pin dự ph&ograve;ng Aukey PB-XD26 26800mAh USB-C Quick Charge 3.0 </strong>cũng sẽ nhanh ch&oacute;ng nạp đầy năng lượng cho thiết bị để bạn tiếp tục qu&aacute; tr&igrave;nh l&agrave;m việc v&agrave; giải tr&iacute; m&agrave; kh&ocirc;ng phải chờ đợi l&acirc;u.</em></p>\r\n\r\n<h3><strong>Thiết kế cao cấp, ngoại h&igrave;nh gọn nhẹ, độ bền cao</strong></h3>\r\n\r\n<p>Pin dự ph&ograve;ng Aukey PB-XD26 c&oacute; k&iacute;ch thước (17,7 x 8,25 x 2,2 cm) v&agrave; trọng lượng (499 g) kh&aacute; l&yacute; tưởng, thuận tiện để bạn cầm nắm hoặc bỏ v&agrave;o balo, t&uacute;i x&aacute;ch mang theo khi đi chơi.</p>\r\n\r\n<p><img alt=\"Thiết kế cao cấp, ngoại hình gọn nhẹ, độ bền cao\" class=\"cpslazy loaded\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/accessories/power_bank/Pin-du-phong-AUKEY-PB-XD26-1.jpg\" style=\"height:600px; width:100%\" /></p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute;, pin được ho&agrave;n thiện tốt ở từng chi tiết tr&ecirc;n bề mặt, tạo n&ecirc;n sự cao cấp v&agrave; chắc chắn, chống va đập tốt, đảm bảo độ bền qua từng năm th&aacute;ng.</p>\r\n\r\n<h3><strong>Nhiều c&ocirc;ng nghệ hiện đại, hỗ trợ sạc nhanh, sạc được cả laptop</strong></h3>\r\n\r\n<p>Pin dự ph&ograve;ng Aukey PB-XD26 được trang bị h&agrave;ng loạt c&ocirc;ng nghệ ti&ecirc;n tiến như AiPower, <a href=\"https://cellphones.com.vn/sforum/qualcomm-trinh-lang-cong-nghe-quick-charge-3-0-hieu-nang-tang-38-so-voi-the-he-truoc-se-co-mat-tren-snapdragon-820?utm_source=sforum&amp;utm_medium=tin-tuc\" target=\"_blank\" title=\"Qualcomm trình làng công nghệ Quick Charge 3.0\">Qualcomm Quick Charge 3.0</a> v&agrave; Power Delivery 3.0. Nhờ vậy, vi&ecirc;n <a href=\"https://cellphones.com.vn/phu-kien/pin-du-phong/aukey.html\" target=\"_blank\" title=\"Pin dự phòng AUKEY chính hãng\"><strong>pin dự ph&ograve;ng của Aukey</strong></a> c&oacute; thể nạp đầy năng lượng cho mọi thiết bị di động trong thời gian ngắn. Chẳng hạn, với những mẫu iPhone phổ biến như iPhone X, XS, XS Max, XR, n&oacute; c&oacute; thể sạc từ 0% đến 50% chỉ trong v&ograve;ng 30 ph&uacute;t.</p>\r\n\r\n<p><img alt=\"Nhiều công nghệ hiện đại, hỗ trợ sạc nhanh, sạc được cả laptop\" class=\"cpslazy loaded\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/accessories/power_bank/Pin-du-phong-AUKEY-PB-XD26-2.jpg\" style=\"height:600px; width:100%\" /></p>\r\n\r\n<p>Pin Aukey PB-XD26 được thiết kế để hoạt động với hầu hết điện thoại, m&aacute;y t&iacute;nh bảng v&agrave; c&aacute;c thiết bị hỗ trợ USB. Đồng thời, n&oacute; c&oacute; thể sạc cho Macbook cũng như những laptop kh&aacute;c sử dụng cổng <span style=\"color:#333300\"><a href=\"https://www.howtogeek.com/211843/usb-type-c-explained-what-it-is-and-why-youll-want-it/\" rel=\"nofollow\" target=\"_blank\" title=\"Cổng kết nối USB C\"><span style=\"color:#333300\">USB-C</span></a></span> để sạc pin. Như vậy, chỉ với một vi&ecirc;n pin dự ph&ograve;ng, bạn c&oacute; thể sạc pin cho những thiết bị th&ocirc;ng dụng nhất: điện thoại, m&aacute;y t&iacute;nh bảng, m&aacute;y t&iacute;nh x&aacute;ch tay.</p>\r\n\r\n<h3><strong>Dung lượng pin khủng 26800 mAh, sạc được nhiều lần cho thiết bị</strong></h3>\r\n\r\n<p>Với dung lượng rất lớn, l&ecirc;n đến 26800 mAh, pin dự ph&ograve;ng Aukey PB-XD26 gi&uacute;p bạn nạp đầy năng lượng nhiều lần cho &ldquo;dế y&ecirc;u&rdquo;. Nếu v&igrave; l&yacute; do g&igrave; đ&oacute; m&agrave; bạn phải &ldquo;ở trong rừng&rdquo; cả ng&agrave;y hoặc thậm ch&iacute; v&agrave;i ng&agrave;y li&ecirc;n tục, chỉ cần mang theo vi&ecirc;n pin Aukey b&ecirc;n m&igrave;nh, bạn sẽ kh&ocirc;ng phải lo lắng về việc sạc pin.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ngo&agrave;i ra, nằm ch&iacute;nh giữa cổng ra &ndash; cổng v&agrave;o của pin l&agrave; đ&egrave;n LED b&aacute;o hiệu. Sẽ c&oacute; 4 mức đ&egrave;n, mỗi mức tương đương 25% cho bạn biết trạng th&aacute;i pin để dễ d&agrave;ng chọn thời điểm sạc lại ph&ugrave; hợp.</p>\r\n\r\n<h3><strong>Tuyệt đối an to&agrave;n, bảo h&agrave;nh 24 th&aacute;ng</strong></h3>\r\n\r\n<p>Pin dự ph&ograve;ng Aukey PB-XD26 được đảm bảo sạc nhanh nhưng cũng hết sức an to&agrave;n, l&agrave; sự lựa chọn đ&aacute;ng tin cậy nhờ &aacute;p dụng c&ocirc;ng nghệ Aukey Entire Protect Technology &ndash; giữ ổn định d&ograve;ng điện v&agrave; nhiệt độ, tr&aacute;nh để xảy ra hiện tượng qu&aacute; n&oacute;ng hay nguy hiểm hơn l&agrave; ch&aacute;y nổ g&acirc;y hại cho người d&ugrave;ng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Như một lời khẳng định về chất lượng sản phẩm, Aukey &aacute;p dụng chế độ bảo h&agrave;nh l&ecirc;n đến 24 th&aacute;ng, tức gấp đ&ocirc;i thời gian bảo h&agrave;nh th&ocirc;ng thường của hầu hết c&aacute;c loại pin dự ph&ograve;ng kh&aacute;c (chỉ 12 th&aacute;ng).</p>\r\n\r\n<h3><strong>Mua pin dự ph&ograve;ng Aukey PB-XD26 26800mAh USB-C Quick Charge 3.0 gi&aacute; rẻ tại <a href=\"https://cellphones.com.vn/\" target=\"_blank\" title=\"Hệ thống cửa hàng bán lẻ thiết bị công nghệ - điện tử chính hãng\">CellphoneS</a></strong></h3>\r\n</div>', '{\"Dòng điện vào\":\"(Micro-USB & Type-C): 5V\\/2.1A, 9V\\/2.1A, 12V\\/1.5A\",\"Dòng điện ra\":\"Khi sạc 1 cổng : 5.1V\\/2.4A, 9V\\/2A, 12V\\/1.5A. Max Khi sạc 2 cổng: 5.1V\\/3.6A\",\"Pin\":\"20000Mah\",\"Tính năng khác\":\"Tích hợp bảo vệ mạch thông minh Chế độ sạc nhỏ giọt\",\"Hãng sản xuất\":\"Xiaomi\"}', 1, 7, 70, 1, '2021-07-15 20:05:34', '2021-07-17 11:54:49', NULL);
INSERT INTO `products` VALUES (581, 'Pin sạc dự phòng Aukey PB-N83S 18W PD 10.000mAh', 'pin-sac-du-phong-aukey-pb-n83s-18w-pd-10000mah', 110, 200000, 490000, 0, '<div class=\"blog-content\">\r\n<h2 style=\"text-align:justify\"><strong>Pin sạc dự ph&ograve;ng Aukey PB &ndash; N83S 18W PD 10000mAH &ndash; Dung lượng cực lớn</strong></h2>\r\n\r\n<p style=\"text-align:justify\">Sạc dự ph&ograve;ng l&agrave; một phụ kiện bổ sung năng lượng cho điện thoại những l&uacute;c khẩn cấp. Nếu bạn đang t&igrave;m phụ kiện tương tự vậy h&atilde;y tham khảo ngay sản phẩm<strong><a href=\"https://cellphones.com.vn/phu-kien/pin-du-phong/aukey.html\" title=\"Pin sạc dự phòng Aukey\"> pin sạc dự ph&ograve;ng Aukey</a> PB &ndash; N83S 18W PD 10000mAH</strong>.</p>\r\n\r\n<h3 style=\"text-align:justify\"><strong>Sạc pin nhanh ch&oacute;ng với c&ocirc;ng suất 18W</strong></h3>\r\n\r\n<p style=\"text-align:justify\">Sản phẩm pin sạc sự ph&ograve;ng Aukey 10000mAH PD 18W &amp; QC 3.0 hỗ trợ c&ocirc;ng nghệ sạc Power Delivery 18W v&agrave; Quick Charge 3. Với c&ocirc;ng nghệ sạc nhanh n&agrave;y, người d&ugrave;ng kh&ocirc;ng chỉ c&oacute; thể nạp điện cho thiết bị điện tử một c&aacute;c nhanh ch&oacute;ng v&agrave; cũng r&uacute;t ngắn thời gian bổ sung dung lượng pin.</p>\r\n\r\n<p style=\"text-align:justify\"><img alt=\"Pin sạc dự phòng Aukey PB – N83S 18W PD 10000mAH\" class=\"cpslazy loaded\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/accessories/charger/pin-sac-du-phong-aukey-pb-n83s-18w-pd-10000mah-1.jpg\" style=\"height:600px; width:100%\" /></p>\r\n\r\n<h3 style=\"text-align:justify\"><strong>Thiết kế nhỏ gọn, dung lượng pin lớn</strong></h3>\r\n\r\n<p style=\"text-align:justify\">Pin sạc dự ph&ograve;ng Aukey PB &ndash; N83S 18W PD 10000mAH với dung lượng pin l&ecirc;n đến 10000 mAh nhờ đ&oacute; c&oacute; thể bổ sung nhiều lần sạc đầy cho điện thoại chỉ với một lần sạc pin.</p>\r\n\r\n<p style=\"text-align:justify\"><img alt=\"Pin sạc dự phòng Aukey PB – N83S 18W PD 10000mAH\" class=\"cpslazy loaded\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/accessories/charger/pin-sac-du-phong-aukey-pb-n83s-18w-pd-10000mah-2.jpg\" style=\"height:600px; width:100%\" /></p>\r\n\r\n<p style=\"text-align:justify\">Pin dự ph&ograve;ng với thiết kế nhỏ gọn, bề mặt chống b&aacute;m v&acirc;n tay một c&aacute;ch hiệu quả. Sạc cũng rất an to&agrave;n khi sử dụng khi được trang bị nhiều c&ocirc;ng nghệ như chống qu&aacute; nhiệt, ngắt mạch, sạc tr&agrave;n, sạc ngược một c&aacute;ch hiệu quả.</p>\r\n</div>', '{\"Dòng điện vào\":\"(Micro-USB & Type-C): 5V\\/2.1A, 9V\\/2.1A, 12V\\/1.5A\",\"Dòng điện ra\":\"Khi sạc 1 cổng : 5.1V\\/2.4A, 9V\\/2A, 12V\\/1.5A. Max Khi sạc 2 cổng: 5.1V\\/3.6A\",\"Pin\":\"20000Mah\",\"Tính năng khác\":\"Tích hợp bảo vệ mạch thông minh Chế độ sạc nhỏ giọt\",\"Hãng sản xuất\":\"Xiaomi\"}', 1, 7, 70, 1, '2021-07-15 22:52:53', '2021-07-17 11:54:49', NULL);
INSERT INTO `products` VALUES (582, 'Pin Sạc dự phòng Aukey QC 3.0 & PD 18W 10000Mah', 'pin-sac-du-phong-aukey-qc-30-pd-18w-10000mah', 110, 200000, 490000, 0, '<div class=\"blog-content\">\r\n<h2><a href=\"https://cellphones.com.vn/pin-sac-du-phong-aukey-qc-3-0-18w-10000mah-xd-12.html\" target=\"_blank\" title=\"Pin Sạc dự phòng Aukey QC 3.0 &amp; PD 18W 10000Mah XD-12\">Pin sạc dự phòng Aukey PB-XD12</a> - Ti&ecirc;́p sức dài l&acirc;u cho đi&ecirc;̣n thoại của bạn</h2>\r\n\r\n<p><em>Là m&ocirc;̣t trong những món <a href=\"https://cellphones.com.vn/phu-kien.html\" target=\"_blank\" title=\"Phụ kiện công nghệ chính hãng\">phụ ki&ecirc;̣n</a> kh&ocirc;ng th&ecirc;̉ thi&ecirc;́u của b&acirc;́t kỳ tín đ&ocirc;̀ smartphone,<a href=\"https://cellphones.com.vn/phu-kien/pin-du-phong.html\" target=\"_blank\" title=\"Pin dự phòng chính hãng\"> pin dự phòng</a> đang d&acirc;̀n trở n&ecirc;n cao c&acirc;́p hơn và có hi&ecirc;̣u năng t&ocirc;́t hơn. Đ&ecirc;̉ có th&ecirc;̉ trang bị cho đi&ecirc;̣n thoại m&ocirc;̣t củ sạc dự phòng hi&ecirc;̣u năng cao, sạc nhanh chóng và sử dụng an toàn, sản ph&acirc;̉m </em><em><strong>pin sạc dự phòng Aukey PB-XD12 10000mAh</strong> sẽ đáp ứng t&ocirc;́t nhu c&acirc;̀u &acirc;́y.</em></p>\r\n\r\n<h3>Hỗ trợ hai c&ocirc;ng nghệ sạc nhanh Quick Charge 3.0 và Power Delivery</h3>\r\n\r\n<p>Y&ecirc;́u t&ocirc;́ cao c&acirc;́p tr&ecirc;n pin <a href=\"https://cellphones.com.vn/phu-kien/pin-du-phong/aukey.html\" target=\"_blank\" title=\"Sạc dự phòng Aukey chính hãng\">sạc dự phòng Aukey</a> PD-XD12 chính là c&ocirc;ng ngh&ecirc;̣ sạc nhanh <a href=\"https://cellphones.com.vn/sforum/qualcomm-trinh-lang-cong-nghe-quick-charge-3-0-hieu-nang-tang-38-so-voi-the-he-truoc-se-co-mat-tren-snapdragon-820?utm_source=sforum&amp;utm_medium=tin-tuc\" target=\"_blank\" title=\"Qualcomm trình làng công nghệ Quick Charge 3.0\">Quick Charge 3.0</a> 18W. Đ&acirc;y là c&ocirc;ng ngh&ecirc;̣ được thi&ecirc;́t k&ecirc;́ bởi hãng Qualcomm trứ danh, và hi&ecirc;̣u năng sạc với Quick Charge 3.0 được đánh giá nhanh g&acirc;́p 4 l&acirc;̀n so với hi&ecirc;̣u năng sạc th&ocirc;ng thường. Người dùng có th&ecirc;̉ sử dụng tính năng sạc nhanh Quick Charge 3.0 th&ocirc;ng qua 2 c&ocirc;̉ng <span style=\"color:#333300\"><a href=\"https://www.lifewire.com/usb-type-a-connector-2626032\" rel=\"nofollow\" target=\"_blank\" title=\"Cổng kết nối USB type a\"><span style=\"color:#333300\">USB-A</span></a> </span>có sẵn tr&ecirc;n củ sạc.</p>\r\n\r\n<p><img alt=\"Hỗ trợ hai công nghệ sạc nhanh Quick Charge 3.0 và Power Delivery\" class=\"cpslazy loaded\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/accessories/power_bank/pin-sac-du-phong-aukey-10000mah-pb-xd12-1.png\" style=\"height:600px; width:100%\" /></p>\r\n\r\n<p>C&ocirc;ng ngh&ecirc;̣ Power Delivery 18W qua <span style=\"color:#333300\"><a href=\"https://www.howtogeek.com/211843/usb-type-c-explained-what-it-is-and-why-youll-want-it/\" rel=\"nofollow\" target=\"_blank\" title=\"Cổng kết nối USB type C\"><span style=\"color:#333300\">USB-C</span></a></span> hai chi&ecirc;̀u là m&ocirc;̣t tính năng cao c&acirc;́p nữa có tr&ecirc;n củ sạc dự phòng Aukey PB-XD12. Với c&ocirc;ng ngh&ecirc;̣ này, người dùng có th&ecirc;̉ chuy&ecirc;̉n ngu&ocirc;̀n pin, dữ li&ecirc;̣u và tín hi&ecirc;̣u video qua d&acirc;y cáp k&ecirc;́t n&ocirc;́i đảo chi&ecirc;̀u. Đi&ecirc;̣n thoại sẽ được sạc với dòng đi&ecirc;̣n 18W và cung c&acirc;́p cho thi&ecirc;́t bị của người dùng ngu&ocirc;̀n pin chính xác, phù hợp với nhu c&acirc;̀u sử dụng.</p>\r\n\r\n<h3>Vi&ecirc;n pin dung lượng 10000mAh với lõi pin Li-Po an toàn khi sử dụng</h3>\r\n\r\n<p>B&ecirc;n trong là vi&ecirc;n pin dung lượng 10000mAh được làm từ lõi Li-Po. Đ&acirc;y là dạng lõi pin ph&ocirc;̉ bi&ecirc;́n cho laptop và các củ sạc dự phòng hi&ecirc;̣n nay vì nhẹ hơn và b&ecirc;̀n rắn hơn các dạng lõi khác. Lõi pin Li-Po cũng r&acirc;́t hi&ecirc;́m khi xảy ra tình trạng bị rò rỉ khí do c&acirc;́u trúc ch&acirc;́t li&ecirc;̣u silicon-graphene của nó. Do đó mà pin <strong>sạc dự phòng Aukey PB-XD12</strong> r&acirc;́t an toàn trong quá trình sử dụng và tỉ l&ecirc;̣ xảy ra l&ocirc;̃i h&acirc;̀u như là v&ocirc; cùng th&acirc;́p.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ngoài ra, dung lượng pin 10000mAh còn là m&ocirc;̣t mức dung lượng đủ mang lại cho người dùng ngu&ocirc;̀n năng lượng dự trữ cho đi&ecirc;̣n thoại cả ngày dài. Cùng với c&ocirc;ng ngh&ecirc;̣ sạc nhanh, thi&ecirc;́t bị của bạn có th&ecirc;̉ sạc được 50% pin cho máy chỉ trong 30 phút.</p>\r\n\r\n<h3>Kích thước nhỏ gọn cùng h&ecirc;̣ th&ocirc;́ng đèn th&ocirc;ng báo LED bắt mắt b&ecirc;n ngoài</h3>\r\n\r\n<p>Kh&ocirc;ng ai mu&ocirc;́n củ sạc dự phòng của mình trở n&ecirc;n c&ocirc;̀ng k&ecirc;̀nh khi c&acirc;̀m tr&ecirc;n tay cả. Và tr&ecirc;n pin sạc dự phòng Aukey PD-XD12 10000mAh, đ&ocirc;̣ mỏng nhẹ đã được đảm bảo. Kích thước của củ sạc là 9.8 x 6.25 x 2.25 cm, với trọng lượng t&ocirc;̉ng là 232g. Lớp vỏ b&ecirc;n ngoài củ sạc cũng làm từ ch&acirc;́t li&ecirc;̣u nh&ocirc;m, nhờ th&ecirc;́ càng tăng th&ecirc;m vẻ cao c&acirc;́p cho phụ ki&ecirc;̣n cũng như hạn ch&ecirc;́ bám v&acirc;n tay &amp; tr&acirc;̀y xước t&ocirc;́t.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Nhằm đưa th&ocirc;ng báo rõ ràng v&ecirc;̀ mức pin hi&ecirc;̣n có, pin sạc dự phòng Aukey 10000mAh PB-XD12 còn được trang bị h&ecirc;̣ th&ocirc;́ng đèn LED hi&ecirc;̉n thị bắt mắt giúp ki&ecirc;̉m tra dung lượng pin tức thì. Người dùng có th&ecirc;̉ kích hoạt tính năng ki&ecirc;̉m tra dung lượng pin qua nút kích hoạt ngu&ocirc;̀n ở b&ecirc;n cạnh các c&ocirc;̉ng k&ecirc;́t n&ocirc;́i.</p>\r\n</div>', '{\"Dòng điện vào\":\"(Micro-USB & Type-C): 5V\\/2.1A, 9V\\/2.1A, 12V\\/1.5A\",\"Dòng điện ra\":\"Khi sạc 1 cổng : 5.1V\\/2.4A, 9V\\/2A, 12V\\/1.5A. Max Khi sạc 2 cổng: 5.1V\\/3.6A\",\"Pin\":\"20000Mah\",\"Tính năng khác\":\"Tích hợp bảo vệ mạch thông minh Chế độ sạc nhỏ giọt\",\"Hãng sản xuất\":\"Xiaomi\"}', 1, 7, 70, 1, '2021-07-15 22:55:45', '2021-07-17 11:54:49', NULL);
INSERT INTO `products` VALUES (583, 'Pin sạc dự phòng Energizer Fabric 10.000mAh (Xám)', 'pin-sac-du-phong-energizer-fabric-10000mah-xam', 110, 200000, 450000, 0, '<div class=\"blog-content\">\r\n<h2><strong><a href=\"https://cellphones.com.vn/pin-du-phong-energizer-10000-mah-ue10043-vo-vai-fabrich.html\" target=\"_blank\" title=\"pin dự phòng Energizer UE10043 10.000mah\">Energizer UE10043 vỏ vải Fabric</a>: Dung lượng lớn, thi&ecirc;́t k&ecirc;́ sang trọng</strong></h2>\r\n\r\n<p><em>Ng&agrave;y nay với nhu cầu sử dụng <a href=\"https://cellphones.com.vn/phu-kien/pin-du-phong.html\" target=\"_blank\" title=\"Sạc dự phòng chính hãng\">pin sạc dự ph&ograve;ng</a> v&agrave; việc sạc đầy lại c&aacute;c thiết bị điện tử của m&igrave;nh ng&agrave;y c&agrave;ng nhiều. Một trong những nh&agrave; sản xuất pin sạc dự ph&ograve;ng h&agrave;ng đầu tại Mỹ đ&atilde; cho ra mắt sản phẩm pin <strong>sạc dự ph&ograve;ng Energizer 10.000 mAh UE10043 vỏ vải Fabric</strong>&nbsp;với thiết kế độc đ&aacute;o c&ugrave;ng c&aacute;c t&iacute;nh năng bảo vệ người d&ugrave;ng cực kỳ tốt.</em></p>\r\n\r\n<h3><strong>Thiết kế độc đ&aacute;o với chất liệu vải Fabric cho khả năng cầm nắm tốt</strong></h3>\r\n\r\n<p>Energizer 10.000 mAh UE10043 mang trong m&igrave;nh một thiết kế bằng nhựa cao cấp b&ecirc;n trong, c&ugrave;ng với lớp vải Fabric phủ b&ecirc;n ngo&agrave;i. Lớp vải này gi&uacute;p việc chống trầy xước v&agrave; chống được c&aacute;c va đập mạnh cũng trở n&ecirc;n dễ d&agrave;ng hơn với sản phẩm sạc dự ph&ograve;ng đến từ thương hiệu Energizer n&agrave;y. Việc trang bị lớp vải Fabric n&agrave;y cũng gi&uacute;p l&agrave;m tăng t&iacute;nh thẩm mỹ v&agrave; độ bền của sản phẩm sạc dự ph&ograve;ng Energizer 10.000 mAh UE10043.</p>\r\n\r\n<p><img alt=\"Thiết kế độc đáo với chất liệu vải Fabric cùng khả năng hạn chế rơi rớt\" class=\"cpslazy loaded\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/accessories/power_bank/pin-sac-du-phong-energizer-vo-vai-fabric-10000mah-ue10043-1.jpg\" style=\"height:600px; width:100%\" /></p>\r\n\r\n<p>Lớp vải Fabric với chức năng ch&iacute;nh l&agrave; trang tr&iacute; v&agrave; tạo t&iacute;nh thẩm mỹ cho <a href=\"https://cellphones.com.vn/phu-kien/pin-du-phong/energizer.html\" target=\"_blank\" title=\"Sạc dự phòng Energizer chính hãng\">sạc dự phòng Energizer</a> 10.000 mAh UE10043 th&igrave; ch&uacute;ng c&ograve;n gi&uacute;p người d&ugrave;ng c&oacute; thể cầm nắm một c&aacute;ch dễ d&agrave;ng hơn. Việc n&agrave;y c&oacute; thể gi&uacute;p bạn hạn chế được việc trơn tuột v&agrave; l&agrave;m rơi rớt sản phẩm sạc dự ph&ograve;ng n&agrave;y gi&uacute;p tăng độ bền của sản phẩm sạc dự ph&ograve;ng l&ecirc;n một c&aacute;ch đ&aacute;ng kể v&agrave; c&oacute; thể giảm được lực t&aacute;c động.</p>\r\n\r\n<h3><strong>Dung lượng 10.000mAh lớn đủ cho người d&ugrave;ng v&agrave; Auto Voltage Sensing th&iacute;ch ứng thiết bị </strong></h3>\r\n\r\n<p>Đa số c&aacute;c m&aacute;y điện thoại hay c&aacute;c thiết bị li&ecirc;n lạc chỉ c&oacute; mức pin tối đa l&agrave; 5000mAh, cho n&ecirc;n trang bị cho m&igrave;nh một vi&ecirc;n pin sạc dự ph&ograve;ng 10.000mAh l&agrave; một điều kh&aacute; hợp l&yacute;. Khi bạn c&oacute; thể sạc l&ecirc;n đến 2 lần cho một thiết bị điện thoại di động của m&igrave;nh. Ngo&agrave;i ra, sạc Energizer 10.000 mAh UE10043 vỏ vải FABRIC với mức dung lượng 10.000mAh bạn c&ograve;n c&oacute; thể sạc th&ecirc;m một thiết bị ngoại vi kh&aacute;c của m&igrave;nh</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Với chức năng Auto Voltage Sensing được trang bị độc quyền tr&ecirc;n c&aacute;c mẫu sạc dự ph&ograve;ng của Energizer gi&uacute;p bạn c&oacute; thể c&oacute; được trải nghiệm sạc c&aacute;c thiết bị l&agrave; như nhau. Ch&uacute;ng gi&uacute;p cho thiết bị của bạn c&oacute; thể th&iacute;ch nghi được với vi&ecirc;n pin sạc dự ph&ograve;ng v&agrave; gi&uacute;p cho nguồn điện ra v&agrave;o của chiếc sạc dự ph&ograve;ng kh&ocirc;ng bị lỗi hay c&oacute; trường hợp đoản mạch n&agrave;o.</p>\r\n\r\n<h3><strong>T&iacute;ch hợp 2 cổng sạc tiện lợi v&agrave; việc sạc ngược cục sạc dễ d&agrave;ng</strong></h3>\r\n\r\n<p>Bằng việc t&iacute;ch hợp cổng sạc ra cho c&aacute;c thiết bị, n&oacute;i l&ecirc;n t&iacute;nh đa dụng của chiếc sạc dự ph&ograve;ng Energizer 10.000 mAh UE10043 vỏ vải FABRIC.&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>D&ugrave; bạn l&agrave; người chuy&ecirc;n sử dụng loại c&aacute;p sạc <span style=\"color:#333300\"><a href=\"https://sea.pcmag.com/laptops/2147/what-is-usb-c-an-explainer\" rel=\"nofollow\" target=\"_blank\" title=\"Cổng kết nối USB type C\"><span style=\"color:#333300\">USB-C</span></a></span> hay l&agrave; <span style=\"color:#333300\"><a href=\"https://whatis.techtarget.com/definition/micro-USB\" rel=\"nofollow\" target=\"_blank\" title=\"Cổng kết nối Micro USB\"><span style=\"color:#333300\">Micro-USB</span></a></span> đi nữa th&igrave; với thiết bị sạc dự ph&ograve;ng đến từ Energizer bạn kh&ocirc;ng cần phải lo lắng qu&aacute; nhiều nếu bạn sạc ch&uacute;ng bằng một trong hai loại d&acirc;y sạc tr&ecirc;n cho cục sạc. Tr&ecirc;n vi&ecirc;n pin dự ph&ograve;ng n&agrave;y ch&uacute;ng được trang bị c&ugrave;ng l&uacute;c 2 cổng sạc USB-C sạc Micro-USB gi&uacute;p bạn thuận tiện hơn.</p>\r\n\r\n<h3><strong>Đ&egrave;n LED th&ocirc;ng b&aacute;o t&igrave;nh trạng pin nổi bật c&ugrave;ng đầu ra sạc nhanh 12W </strong></h3>\r\n\r\n<p>Cụm 4 đ&egrave;n led th&ocirc;ng b&aacute;o tr&ecirc;n Energizer 10.000 mAh UE10043 vỏ vải FABRIC n&agrave;y c&oacute; khả năng hiển thị đ&egrave;n led trắng. Mỗi mức led sẽ tương ứng với 25% pin, điều n&agrave;y gi&uacute;p bạn c&oacute; thể hiểu r&otilde; hơn t&igrave;nh trạng v&agrave; mức pin c&ograve;n trong chiếc vi&ecirc;n pin sạc dự ph&ograve;ng của m&igrave;nh. Ngo&agrave;i ra, b&ecirc;n h&ocirc;ng ph&iacute;a đối diện của cụm đ&egrave;n led c&ograve;n c&oacute; một n&uacute;t bấm gi&uacute;p hiển thị đ&egrave;n led.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>B&ecirc;n cạnh nhiều t&iacute;nh năng nổi trội n&oacute;i tr&ecirc;n, th&igrave; một trong những t&iacute;nh năng quan trọng của Energizer 10.000 mAh UE10043 kh&ocirc;ng những cung cấp một lượng pin cần thiết cho người d&ugrave;ng m&agrave; ch&uacute;ng c&ograve;n c&oacute; thể sạc nhanh gi&uacute;p tiết kiệm được thời gian qu&yacute; b&aacute;u của người sở hữu ch&uacute;ng. Với đầu ra 12W tức 5V/2.4A cho mỗi cồng sạc sẽ gi&uacute;p bạn sạc nhanh hơn.</p>\r\n\r\n<h3><strong>Sản phẩm ch&iacute;nh h&atilde;ng Energizer nổi tiếng c&ugrave;ng c&aacute;c chứng chỉ chống ch&aacute;y nổ an to&agrave;n</strong></h3>\r\n\r\n<p>Energizer l&agrave; một trong những h&atilde;ng sản xuất pin với tuổi đời l&ecirc;n đến hơn 100 năm trong lĩnh vực sản xuất pin. Do đ&oacute;, khi nhắc đến c&aacute;c loại pin sạc dự ph&ograve;ng hay c&aacute;c sản phẩm, <a href=\"https://cellphones.com.vn/phu-kien.html\" target=\"_blank\" title=\"Phụ kiện công nghệ chính hãng\">phụ ki&ecirc;̣n</a> về pin của Energizer l&agrave; phải nhắc đến sự bền bỉ, chất lượng v&agrave; độ an to&agrave;n cao. Với nhiều năm kinh nghiệm trong lĩnh vực sản xuất pin bạn sẽ cảm thấy y&ecirc;n t&acirc;m khi sử dụng ch&uacute;ng.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Để đảm bảo độ an to&agrave;n v&agrave; cũng như sự t&iacute;n nhiệm của người d&ugrave;ng với c&aacute;c sản phẩm của Energizer th&igrave; thương hiệu c&ograve;n cung cấp c&aacute;c chứng nhận đảm bảo độ an to&agrave;n v&agrave; ph&ograve;ng chống ch&aacute;y nổ như: CE, FCC, ETL, CB, EAC, RoHS, Reach, ERP6, DOE6. Với c&aacute;c chứng chỉ n&agrave;y bạn c&oacute; thể cảm thấy bạn lu&ocirc;n được bảo vệ an to&agrave;n bởi ch&iacute;nh thương hiệu.</p>\r\n</div>', '{\"Dòng điện vào\":\"(Micro-USB & Type-C): 5V\\/2.1A, 9V\\/2.1A, 12V\\/1.5A\",\"Dòng điện ra\":\"Khi sạc 1 cổng : 5.1V\\/2.4A, 9V\\/2A, 12V\\/1.5A. Max Khi sạc 2 cổng: 5.1V\\/3.6A\",\"Pin\":\"20000Mah\",\"Tính năng khác\":\"Tích hợp bảo vệ mạch thông minh Chế độ sạc nhỏ giọt\",\"Hãng sản xuất\":\"Xiaomi\"}', 1, 7, 26