INSERT INTO `products` VALUES (647, 'Apple Watch Series 6 44mm (4G) Viền Thép Dây Thép', 'apple-watch-series-6-44mm-4g-vien-thep-day-thep', 160, 12000000, 19690000, 0, '<div class=\"blog-content\">\r\n<h2><strong>Apple Watch Series 6 40mm - Sự n&acirc;ng cấp đ&aacute;ng kể về ngoại h&igrave;nh lẫn c&ocirc;ng nghệ</strong></h2>\r\n\r\n<p>Hầu hết t&iacute;n đồ của Apple Watch đều muốn sở hữu ngay cho m&igrave;nh một chiếc <a href=\"https://cellphones.com.vn/do-choi-cong-nghe/apple-watch/series-6.html\" target=\"_self\" title=\"Đồng hồ Apple Watch Series 6\">Apple Watch Series 6</a> 40mm mới nhất trong năm 2020 bởi n&oacute; đ&atilde; được n&acirc;ng cấp kh&aacute; nhiều về ngoại h&igrave;nh v&agrave; c&ocirc;ng nghệ. Mang đến một vẻ ngo&agrave;i ho&agrave;n to&agrave;n mới, tiện lợi hơn với sự n&acirc;ng cấp đ&aacute;ng kể chức năng.</p>\r\n\r\n<h3><strong>Vừa vặn khung kim loại nh&ocirc;m nguy&ecirc;n khối, m&agrave;n h&igrave;nh Always-On Retina mới</strong></h3>\r\n\r\n<p>So với c&aacute;c d&ograve;ng thế hệ trước lần n&agrave;y Apple Watch Series 6 40mm cũng kh&ocirc;ng c&oacute; g&igrave; thay đổi về chất liệu thiết kế đ&oacute; l&agrave; khung kim loại nh&ocirc;m nguy&ecirc;n khối. Điều đặc biệt l&agrave; n&oacute; thước thiết kế c&acirc;n đối ch&iacute;nh x&aacute;c hơn.</p>\r\n\r\n<p>Mang đến một c&aacute;i nh&igrave;n vừa vặn khung viền kh&ocirc;ng qu&aacute; to v&agrave; v&agrave; c&acirc;n đối.</p>\r\n\r\n<p><img alt=\"Vừa vặn khung kim loại nhôm nguyên khối\" class=\"cpslazy loaded\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/accessories/watch_band/apple-watch-series-6-2_1.jpg\" style=\"height:60%; width:100%\" /></p>\r\n\r\n<p>M&agrave;n h&igrave;nh tr&ecirc;n Apple Watch Series 6 40mm đ&atilde; được sử dụng loại Always-On Retina mới mang đến khả năng hiển thị sắc n&eacute;t hơn c&aacute;c thế hệ trước nhiều tỉ lệ viền cũng c&acirc;n đối mỏng hơn b&igrave;nh thường đẹp hơn.</p>\r\n\r\n<p>Ngo&agrave;i ra với k&iacute;ch thước 40mm m&agrave;n h&igrave;nh của chiếc đồng hồ n&agrave;y mang đến một kh&ocirc;ng gian mới rộng hơn khi thiết kế tr&agrave;n viền v&agrave; chuẩn hơn.</p>\r\n\r\n<h3><strong>Chip Apple S6 mạnh mẽ, chống nước 5 ATM (50m)</strong></h3>\r\n\r\n<p>L&agrave; phi&ecirc;n bản mới nhất trong năm 2020 Apple đ&atilde; trang bị cho Apple Watch Series 6 40mm con chip Apple S6 với tốc độ xử l&yacute; cực nhanh v&agrave; tương th&iacute;ch cải thiện khả năng c&aacute;c chức năng rất nhiều khi sử dụng hằng ng&agrave;y.</p>\r\n\r\n<p><img alt=\"Chống nước 5ATM\" class=\"cpslazy loaded\" src=\"https://cdn.cellphones.com.vn/media/wysiwyg/accessories/watch_band/apple-watch-series-6-3_1.jpg\" style=\"height:60%; width:100%\" /></p>\r\n\r\n<p>Đặc biệt chiếc Apple Watch Series 6 40mm cũng đ&atilde; được sử dụng c&ocirc;ng nghệ chống nước đạt chuẩn 5ATM cho khả năng sử dụng ở độ s&acirc;u l&ecirc;n đến 50m m&agrave; kh&ocirc;ng ảnh hưởng, mang đến trải nghiệm th&uacute; vị cho người d&ugrave;ng.</p>\r\n\r\n<h3><strong>Pin sử dụng l&ecirc;n đến 18 giờ, nhiều cảm biến quan trọng cho sức khỏe</strong></h3>\r\n\r\n<p>Pin tr&ecirc;n Apple Watch Series 6 40mm đ&atilde; được n&acirc;ng cấp kh&aacute; nhiều khi trang bị vi&ecirc;n pin c&oacute; dung lượng lớn mang đến thời gian sử dụng d&agrave;i li&ecirc;n tục đạt 18 giờ v&agrave; cho khả năng sạc đầy nhanh ch&oacute;ng trong 1.5 giờ đầy pin.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ở mặt dưới của đồng hồ Apple Watch Series 6 40mm h&atilde;ng đ&atilde; n&acirc;ng cấp c&aacute;c cảm biến mới nhất: cảm biến nhịp tim, cảm biến độ cao với độ ch&iacute;nh x&aacute;c gần như tuyệt đối.&nbsp;Với những cảm biến n&agrave;y sức khỏe của bạn sẽ được cập nhật li&ecirc;n tục v&agrave; gửi dữ liệu về ph&acirc;n t&iacute;ch ch&iacute;nh x&aacute;c nhất v&agrave; đưa ra những cảnh b&aacute;o cho bạn sớm để ph&ograve;ng tr&aacute;nh trong cuộc sống hằng ng&agrave;y khi sức khỏe c&oacute; vấn đề.</p>\r\n\r\n<h3><strong>Hỗ trợ nhiều t&iacute;nh năng tiện lợi cho cuộc sống</strong></h3>\r\n\r\n<p>Cũng giống như c&aacute;c thế hệ trước chắc chắn Apple Watch Series 6 40mm sẽ được trang bị chức năng đo nhịp tim, đo điện t&acirc;m đồ ECG cập nhật li&ecirc;n tục để bảo vệ sức khỏe của bạn.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Ngo&agrave;i ra chiếc đồng hồ n&agrave;y c&ograve;n c&oacute; thể nghe nhạc tr&ecirc;n Apple Music sử dụng c&aacute;c t&iacute;nh năng luyện tập thể dục hay đổi mặt đồng hồ với nhiều kiểu bắt mắt v&agrave; năng động hơn khi sử dụng đeo hằng ng&agrave;y.</p>\r\n</div>', '{\"Kích thước màn hình\":\"1.57 inch\",\"Độ phân giải\":\"324 x 394 pixels\",\"Công nghệ màn hình\":\"LTPO OLED display (1000 nits)\",\"Bộ nhớ trong\":\"32 GB\",\"Chống nước\":\"Có, độ sâu dưới 50m\",\"Chipset\":\"Apple S5\",\"Loại CPU\":\"Dual-core\",\"GPU\":\"PowerVR\",\"Bluetooth\":\"5.0, A2DP, LE\",\"Kích thước\":\"44 x 38 x 10.4 mm\",\"Trọng lượng\":\"36.4 g\",\"Hãng sản xuất\":\"Apple\"}', 1, 5, 5, 1, '2021-07-17 11:31:34', '2021-07-17 11:54:25', NULL);
